mapper.select:
select /* 综合-价格-根据主键查询计费规则*/

from PKP.T_SRV_PRICING_VALUATION MASTER
where MASTER.ID = #{id,jdbcType=VARCHAR}


			SELECT /* 综合-价格-根据综合条件查询计费规则*/



    FROM PKP.T_SRV_PRICING_VALUATION MASTER



			MASTER.PRICING_ENTRY_CODE = #{pricingEntryCode,jdbcType=VARCHAR}


			AND MASTER.PRICE_PLAN_ID = #{pricePlanId,jdbcType=VARCHAR}


			AND MASTER.TYPE = #{type,jdbcType=VARCHAR}


			AND MASTER.LONG_OR_SHORT = #{longOrShort,jdbcType=VARCHAR}


			AND MASTER.ACTIVE = #{active,jdbcType=VARCHAR}


			AND MASTER.END_TIME = #{endTime,jdbcType=TIMESTAMP} 


			AND MASTER.END_TIME >= #{beginTime,jdbcType=TIMESTAMP}
			AND MASTER.BEGIN_TIME   <=  #{beginTime,jdbcType=TIMESTAMP}


			AND MASTER.GOODS_TYPE_ID = #{goodsTypeId,jdbcType=VARCHAR} 


			AND MASTER.PRODUCT_ID = #{productId,jdbcType=VARCHAR}


			AND MASTER.PRODUCT_CODE = #{productCode,jdbcType=VARCHAR}


			AND MASTER.MARKETING_EVENT_ID = #{marketingEventId,jdbcType=VARCHAR}


			AND MASTER.MARKETING_EVENT_CODE = #{marketingEventCode,jdbcType=VARCHAR}


			AND MASTER.DEPT_REGION_ID = #{deptRegionId,jdbcType=VARCHAR}


			AND MASTER.ARRV_REGION_ID = #{arrvRegionId,jdbcType=VARCHAR}


			AND MASTER.ID IN 

				#{valuationId}  



	ORDER BY MASTER.MODIFY_TIME DESC


			SELECT /* 综合-价格-根据综合条件查询计费规则*/



    FROM PKP.T_SRV_PRICING_VALUATION MASTER



			MASTER.PRICING_ENTRY_CODE = #{pricingEntryCode,jdbcType=VARCHAR}


			AND MASTER.PRICE_PLAN_ID = #{pricePlanId,jdbcType=VARCHAR}


			AND MASTER.TYPE = #{type,jdbcType=VARCHAR}


			AND MASTER.LONG_OR_SHORT = #{longOrShort,jdbcType=VARCHAR}


			AND MASTER.ACTIVE = #{active,jdbcType=VARCHAR}


			AND MASTER.END_TIME >= #{endTime,jdbcType=TIMESTAMP}
			AND MASTER.BEGIN_TIME   <  #{beginTime,jdbcType=TIMESTAMP}+1


			AND MASTER.GOODS_TYPE_ID = #{goodsTypeId,jdbcType=VARCHAR} 


			AND MASTER.PRODUCT_ID = #{productId,jdbcType=VARCHAR}


			AND MASTER.PRODUCT_CODE = #{productCode,jdbcType=VARCHAR}


			AND (MASTER.PRODUCT_CODE != 'PACKAGE' OR MASTER.PRODUCT_CODE IS NULL)


			AND MASTER.MARKETING_EVENT_ID = #{marketingEventId,jdbcType=VARCHAR}


			AND MASTER.MARKETING_EVENT_CODE = #{marketingEventCode,jdbcType=VARCHAR}


			AND MASTER.DEPT_REGION_ID = #{deptRegionId,jdbcType=VARCHAR}


			AND MASTER.ARRV_REGION_ID = #{arrvRegionId,jdbcType=VARCHAR}


			AND MASTER.ID IN 

				#{valuationId}  



	ORDER BY MASTER.MODIFY_TIME DESC


			SELECT /* 综合-价格-根据综合条件查询计费规则*/



    FROM PKP.T_SRV_PRICING_VALUATION MASTER



			AND MASTER.TYPE = #{type,jdbcType=VARCHAR}


			AND MASTER.ACTIVE = #{active,jdbcType=VARCHAR}


			AND MASTER.DEPT_REGION_ID = #{deptRegionId,jdbcType=VARCHAR}


			AND MASTER.ARRV_REGION_ID = #{arrvRegionId,jdbcType=VARCHAR}


			AND MASTER.VERSION_NO  >  #{versionNo}        


			AND MASTER.END_TIME  >  #{endTime,jdbcType=TIMESTAMP} 


			AND MASTER.BEGIN_TIME   <=  #{beginTime,jdbcType=TIMESTAMP} 


			ORDER by MASTER.VERSION_NO ASC

SELECT /* 综合-价格-根据综合条件查询计费规则*/
MASTER.ID AS ID , MASTER.GOODS_TYPE_ID AS GOODS_TYPE_ID , MASTER.GOODS_TYPE_CODE AS GOODS_TYPE_CODE , MASTER.PRODUCT_ID AS PRODUCT_ID , MASTER.PRODUCT_CODE AS PRODUCT_CODE , MASTER.DEPT_REGION_ID AS DEPT_REGION_ID , MASTER.ARRV_REGION_ID AS ARRV_REGION_ID , MASTER.SALES_CHANNEL_CODE AS SALES_CHANNEL_CODE , MASTER.SALES_CHANNEL_ID AS SALES_CHANNEL_ID , MASTER.PRICING_ENTRY_ID AS PRICING_ENTRY_ID , MASTER.PRICING_ENTRY_CODE AS PRICING_ENTRY_CODE , MASTER.PRICE_PLAN_ID AS PRICE_PLAN_ID , MASTER.PRICE_PLAN_CODE AS PRICE_PLAN_CODE , MASTER.MARKETING_EVENT_ID AS MARKETING_EVENT_ID , MASTER.MARKETING_EVENT_CODE AS MARKETING_EVENT_CODE , MASTER.DESCRIPTION AS DESCRIPTION , MASTER.VERSION_NO AS VERSION_NO , MASTER.ACTIVE AS ACTIVE , MASTER.BEGIN_TIME AS BEGIN_TIME , MASTER.END_TIME AS END_TIME , MASTER.LONG_OR_SHORT AS LONG_OR_SHORT , MASTER.TYPE AS TYPE , MASTER.CURRENCY_CODE AS CURRENCY_CODE , MASTER.CODE AS CODE , MASTER.NAME AS NAME , MASTER.CREATE_TIME AS CREATE_TIME , MASTER.CREATE_USER_CODE AS CREATE_USER_CODE , MASTER.CREATE_ORG_CODE AS CREATE_ORG_CODE , MASTER.MODIFY_TIME AS MODIFY_TIME , MASTER.MODIFY_USER_CODE AS MODIFYUSER , MASTER.CENTRALIZE_PICKUP AS CENTRALIZE_PICKUP , FLIGHT_SHIFT AS FLIGHT_SHIFT,
DETAIL.ID AS SUBID, DETAIL.NAME AS SUBNAME, DETAIL.CACULATE_TYPE AS SUBCACULATE_TYPE, DETAIL.FEE AS SUBFEE, DETAIL.FEE_RATE AS SUBFEE_RATE, DETAIL.LEFTRANGE AS SUBLEFTRANGE, DETAIL.RIGHTRANGE AS SUBRIGHTRANGE, DETAIL.MIN_FEE AS SUBMIN_FEE, DETAIL.MAX_FEE AS SUBMAX_FEE, DETAIL.SUB_TYPE AS SUBSUB_TYPE, DETAIL.CANMODIFY AS SUBCANMODIFY, DETAIL.CANDELETE AS SUBCANDELETE, DETAIL.DESCRIPTION AS SUBDESCRIPTION, DETAIL.PROCESS_PROGRAM AS SUBPROCESS_PROGRAM, DETAIL.PROCESS_PARM_VAL AS SUBPROCESS_PARM_VAL, DETAIL.PRICING_CRITERIA_ID AS SUBPRICING_CRITERIA_ID, DETAIL.T_SRV_PRICE_RULE_ID AS SUBT_SRV_PRICE_RULE_ID, DETAIL.PARM2 AS SUBPARM2, DETAIL.PARM1 AS SUBPARM1, DETAIL.PARM3 AS SUBPARM3, DETAIL.PARM4 AS SUBPARM4, DETAIL.PARM5 AS SUBPARM5, DETAIL.DISCOUNT_RATE AS SUBDISCOUNT_RATE, DETAIL.ACTIVE AS SUBACTIVE, DETAIL.VERSION_NO AS SUBVERSION_NO, DETAIL.PRICING_VALUATION_ID AS SUBPRICING_VALUATION_ID, DETAIL.DEPT_REGION_ID AS SUBDEPT_REGION_ID
FROM PKP.T_SRV_PRICING_VALUATION MASTER LEFT JOIN PKP.T_SRV_PRICING_CRITERIA_DETAIL DETAIL ON MASTER.ID= DETAIL.pricing_valuation_id
AND MASTER.TYPE = #{type,jdbcType=VARCHAR}
AND MASTER.ACTIVE = #{active,jdbcType=VARCHAR}
AND MASTER.DEPT_REGION_ID = #{deptRegionId,jdbcType=VARCHAR}
AND MASTER.ARRV_REGION_ID = #{arrvRegionId,jdbcType=VARCHAR}
AND MASTER.VERSION_NO >= #{versionNo}
AND MASTER.END_TIME > #{endTime,jdbcType=TIMESTAMP}
AND MASTER.BEGIN_TIME <= #{beginTime,jdbcType=TIMESTAMP}
ORDER by MASTER.VERSION_NO,MASTER.ID ASC


			SELECT /* 综合-价格-根据综合条件查询计费规则*/



    FROM PKP.T_SRV_PRICING_VALUATION MASTER
    LEFT JOIN PKP.T_SRV_PRICING_ENTRY PRICING
    ON PRICING.CODE = MASTER.PRICING_ENTRY_CODE



			MASTER.PRICING_ENTRY_CODE = #{pricingEntryCode,jdbcType=VARCHAR}


			AND MASTER.TYPE = #{type,jdbcType=VARCHAR}


			AND MASTER.LONG_OR_SHORT = #{longOrShort,jdbcType=VARCHAR}


			AND MASTER.ACTIVE = #{active,jdbcType=VARCHAR}


			AND MASTER.END_TIME = #{endTime,jdbcType=TIMESTAMP} 


			AND MASTER.END_TIME > #{beginTime,jdbcType=TIMESTAMP} 


			AND MASTER.GOODS_TYPE_ID = #{goodsTypeId,jdbcType=VARCHAR} 


			AND MASTER.GOODS_TYPE_ID IS NULL


			AND MASTER.PRODUCT_ID = #{productId,jdbcType=VARCHAR}


			AND MASTER.PRODUCT_CODE = #{productCode,jdbcType=VARCHAR}


			AND MASTER.PRODUCT_CODE IS NULL


			AND MASTER.DEPT_REGION_ID = #{deptRegionId,jdbcType=VARCHAR}


			AND MASTER.DEPT_REGION_ID IS NULL


			AND MASTER.ARRV_REGION_ID = #{arrvRegionId,jdbcType=VARCHAR}


			AND MASTER.ARRV_REGION_ID IS NULL


			AND MASTER.ID IN 

				#{valuationId}  



	ORDER BY MASTER.MODIFY_TIME DESC

mapper.update:

	UPDATE /* 综合-价格-激活增值服务*/ 
	pkp.T_SRV_PRICING_VALUATION T
	SET T.ACTIVE = 'Y',
	T.VERSION_NO = #{versionNo,jdbcType=DECIMAL}

WHERE T.ID IN 

		#{valuationId}

mapper.delete:
DELETE /* 综合-价格-删除增值服务 */ FROM pkp.T_SRV_PRICING_VALUATION T
WHERE T.ID IN
#{valuationId}

mapper.select:

			SELECT /* 综合-价格-根据查询条件统计计费规则 */ 
			COUNT(1)


    FROM PKP.T_SRV_PRICING_VALUATION MASTER
    LEFT JOIN PKP.T_SRV_PRICING_ENTRY PRICING
    ON PRICING.CODE = MASTER.PRICING_ENTRY_CODE



			MASTER.PRICING_ENTRY_CODE = #{pricingEntryCode,jdbcType=VARCHAR}


			AND MASTER.PRICE_PLAN_ID = #{pricePlanId,jdbcType=VARCHAR}


			AND MASTER.TYPE = #{type,jdbcType=VARCHAR}


			AND MASTER.LONG_OR_SHORT = #{longOrShort,jdbcType=VARCHAR}


			AND MASTER.ACTIVE = #{active,jdbcType=VARCHAR}


			AND MASTER.END_TIME >= #{endTime,jdbcType=TIMESTAMP}
			AND MASTER.BEGIN_TIME   <  #{beginTime,jdbcType=TIMESTAMP}+1


			AND MASTER.GOODS_TYPE_ID = #{goodsTypeId,jdbcType=VARCHAR} 


			AND MASTER.PRODUCT_ID = #{productId,jdbcType=VARCHAR}


			AND (MASTER.PRODUCT_CODE != 'PACKAGE' OR MASTER.PRODUCT_CODE IS NULL)


			AND MASTER.PRODUCT_CODE = #{productCode,jdbcType=VARCHAR}


			AND MASTER.MARKETING_EVENT_ID = #{marketingEventId,jdbcType=VARCHAR}


			AND MASTER.MARKETING_EVENT_CODE = #{marketingEventCode,jdbcType=VARCHAR}


			AND MASTER.DEPT_REGION_ID = #{deptRegionId,jdbcType=VARCHAR}


			AND MASTER.ARRV_REGION_ID = #{arrvRegionId,jdbcType=VARCHAR}


			AND MASTER.ID IN 

				#{valuationId}

mapper.delete:
delete /* 综合-价格-根据主键删除计费规则 */ 
from PKP.T_SRV_PRICING_VALUATION
where ID = #{id,jdbcType=VARCHAR}

mapper.select:
     SELECT /* 综合-价格-查询运费计算明细  */ 

    	FROM 
    	PKP.T_SRV_PRICE_PLAN pla 
    	INNER JOIN PKP.T_SRV_PRICING_VALUATION va ON pla.id = va.PRICE_PLAN_ID
    	INNER JOIN PKP.T_SRV_PRICING_CRITERIA_DETAIL detail ON va.ID = detail.PRICING_VALUATION_ID
    	INNER JOIN PKP.T_SRV_PRICE_RULE rl ON detail.T_SRV_PRICE_RULE_ID = rl.ID
    	where 
    	pla.T_SRV_PRICE_REGION_ID = #{originalOrgId ,jdbcType=VARCHAR} 
    	AND va.ARRV_REGION_ID = #{destinationId ,jdbcType=VARCHAR} 
    	AND pla.ACTIVE = #{active ,jdbcType=VARCHAR}              
    	AND va.TYPE = #{type ,jdbcType=VARCHAR}  				  
    	AND va.PRODUCT_CODE = #{productCode ,jdbcType=VARCHAR}    

    	AND va.GOODS_TYPE_CODE = #{goodsTypeCode ,jdbcType=VARCHAR}	


    	AND va.CURRENCY_CODE = #{currencyCode ,jdbcType=VARCHAR}	


    	AND va.CENTRALIZE_PICKUP = #{isReceiveGoods ,jdbcType=VARCHAR}	


    	AND va.FLIGHT_SHIFT = #{flightShift ,jdbcType=VARCHAR}	


    		AND detail.COMB_BILL_TYPE_CODE = #{combBillTypeCode ,jdbcType=VARCHAR}	

    	/* 综合-价格-查询运费计算明细-小于截止日期大于等于开始开始日期  */ 

			AND pla.END_TIME  >  #{receiveDate,jdbcType=TIMESTAMP}
			AND pla.BEGIN_TIME   <=  #{receiveDate,jdbcType=TIMESTAMP}


    	SELECT /* 综合-价格-查询增值服务相关计算计费规则与明细信息-组合查询  */ 

    	FROM 
    	PKP.T_SRV_PRICING_VALUATION va INNER JOIN	 
    	PKP.T_SRV_PRICING_CRITERIA_DETAIL detail ON	va.id = detail.PRICING_VALUATION_ID 
    	left join pkp.t_srv_pricing_entry entry on entry.code=detail.sub_type 
    	INNER JOIN PKP.T_SRV_PRICE_RULE rule ON rule.id =  detail.T_SRV_PRICE_RULE_ID  


				AND va.PRICING_ENTRY_CODE = #{pricingEntryCode,jdbcType=VARCHAR} 


				AND va.TYPE = #{type,jdbcType=VARCHAR}        


				AND va.LONG_OR_SHORT = #{longOrShort,jdbcType=VARCHAR}


				AND va.ACTIVE = #{active,jdbcType=VARCHAR}


				AND va.PRODUCT_CODE = #{productCode,jdbcType=VARCHAR} 


				AND va.GOODS_TYPE_CODE = #{goodsTypeCode,jdbcType=VARCHAR} 



				AND (va.DEPT_REGION_ID = #{deptRegionId,jdbcType=VARCHAR} OR va.DEPT_REGION_ID = #{allNet,jdbcType=VARCHAR})



				AND va.DEPT_REGION_ID = #{allNet,jdbcType=VARCHAR}




				AND (va.ARRV_REGION_ID = #{arrvRegionId,jdbcType=VARCHAR} OR va.ARRV_REGION_ID = #{allNet,jdbcType=VARCHAR}) 


				AND va.ARRV_REGION_ID = #{allNet,jdbcType=VARCHAR} 


				AND detail.SUB_TYPE = #{subType,jdbcType=VARCHAR}  





					AND  ( detail.SUB_TYPE IS NULL  or  detail.SUB_TYPE='' )



					AND detail.leftrange * 100  <  #{originnalCost,jdbcType=DECIMAL}
					AND detail.rightRange * 100  >=  #{originnalCost,jdbcType=DECIMAL}


					AND detail.leftrange * 100  =  #{originnalCost,jdbcType=DECIMAL}
					AND detail.rightRange * 100  >=  #{originnalCost,jdbcType=DECIMAL}



				AND #{receiveDate,jdbcType=TIMESTAMP}  >=   va.BEGIN_TIME 
				AND #{receiveDate,jdbcType=TIMESTAMP}  <    va.END_TIME

SELECT /* 综合-价格-根据综合条件查询计费规则*/
MASTER.ID AS ID , MASTER.GOODS_TYPE_ID AS GOODS_TYPE_ID , MASTER.GOODS_TYPE_CODE AS GOODS_TYPE_CODE , MASTER.PRODUCT_ID AS PRODUCT_ID , MASTER.PRODUCT_CODE AS PRODUCT_CODE , MASTER.DEPT_REGION_ID AS DEPT_REGION_ID , MASTER.ARRV_REGION_ID AS ARRV_REGION_ID , MASTER.SALES_CHANNEL_CODE AS SALES_CHANNEL_CODE , MASTER.SALES_CHANNEL_ID AS SALES_CHANNEL_ID , MASTER.PRICING_ENTRY_ID AS PRICING_ENTRY_ID , MASTER.PRICING_ENTRY_CODE AS PRICING_ENTRY_CODE , MASTER.PRICE_PLAN_ID AS PRICE_PLAN_ID , MASTER.PRICE_PLAN_CODE AS PRICE_PLAN_CODE , MASTER.MARKETING_EVENT_ID AS MARKETING_EVENT_ID , MASTER.MARKETING_EVENT_CODE AS MARKETING_EVENT_CODE , MASTER.DESCRIPTION AS DESCRIPTION , MASTER.VERSION_NO AS VERSION_NO , MASTER.ACTIVE AS ACTIVE , MASTER.BEGIN_TIME AS BEGIN_TIME , MASTER.END_TIME AS END_TIME , MASTER.LONG_OR_SHORT AS LONG_OR_SHORT , MASTER.TYPE AS TYPE , MASTER.CURRENCY_CODE AS CURRENCY_CODE , MASTER.CODE AS CODE , MASTER.NAME AS NAME , MASTER.CREATE_TIME AS CREATE_TIME , MASTER.CREATE_USER_CODE AS CREATE_USER_CODE , MASTER.CREATE_ORG_CODE AS CREATE_ORG_CODE , MASTER.MODIFY_TIME AS MODIFY_TIME , MASTER.MODIFY_USER_CODE AS MODIFYUSER , MASTER.CENTRALIZE_PICKUP AS CENTRALIZE_PICKUP , FLIGHT_SHIFT AS FLIGHT_SHIFT,
DETAIL.ID AS SUBID, DETAIL.NAME AS SUBNAME, DETAIL.CACULATE_TYPE AS SUBCACULATE_TYPE, DETAIL.FEE AS SUBFEE, DETAIL.FEE_RATE AS SUBFEE_RATE, DETAIL.LEFTRANGE AS SUBLEFTRANGE, DETAIL.RIGHTRANGE AS SUBRIGHTRANGE, DETAIL.MIN_FEE AS SUBMIN_FEE, DETAIL.MAX_FEE AS SUBMAX_FEE, DETAIL.SUB_TYPE AS SUBSUB_TYPE, DETAIL.CANMODIFY AS SUBCANMODIFY, DETAIL.CANDELETE AS SUBCANDELETE, DETAIL.DESCRIPTION AS SUBDESCRIPTION, DETAIL.PROCESS_PROGRAM AS SUBPROCESS_PROGRAM, DETAIL.PROCESS_PARM_VAL AS SUBPROCESS_PARM_VAL, DETAIL.PRICING_CRITERIA_ID AS SUBPRICING_CRITERIA_ID, DETAIL.T_SRV_PRICE_RULE_ID AS SUBT_SRV_PRICE_RULE_ID, DETAIL.PARM2 AS SUBPARM2, DETAIL.PARM1 AS SUBPARM1, DETAIL.PARM3 AS SUBPARM3, DETAIL.PARM4 AS SUBPARM4, DETAIL.PARM5 AS SUBPARM5, DETAIL.DISCOUNT_RATE AS SUBDISCOUNT_RATE, DETAIL.ACTIVE AS SUBACTIVE, DETAIL.VERSION_NO AS SUBVERSION_NO, DETAIL.PRICING_VALUATION_ID AS SUBPRICING_VALUATION_ID, DETAIL.DEPT_REGION_ID AS SUBDEPT_REGION_ID
FROM PKP.T_SRV_PRICING_VALUATION MASTER LEFT JOIN PKP.T_SRV_PRICING_CRITERIA_DETAIL DETAIL ON MASTER.ID= DETAIL.pricing_valuation_id
MASTER.FLIGHT_SHIFT IS NULL
AND MASTER.TYPE = #{type,jdbcType=VARCHAR}
AND MASTER.ACTIVE = #{active,jdbcType=VARCHAR}
AND MASTER.DEPT_REGION_ID = #{deptRegionId,jdbcType=VARCHAR}
AND MASTER.VERSION_NO >= #{versionNo}
AND MASTER.END_TIME > #{endTime,jdbcType=TIMESTAMP}
AND MASTER.BEGIN_TIME <= #{beginTime,jdbcType=TIMESTAMP}
ORDER by MASTER.VERSION_NO,MASTER.ID ASC

