mapper.select:

	SELECT
	/** 根据管理部门查询接送货大区  **/



FROM BSE.T_BAS_SERVICE_BIGZONE BIGZONE

	 ACTIVE = 'Y'

		and REGION_CODE like
		CONCAT(CONCAT('%',#{regionCode,jdbcType=VARCHAR}),'%')


		and REGION_NAME like
		CONCAT(CONCAT('%',#{regionName,jdbcType=VARCHAR}),'%')


		and MANAGEMENT = #{management,jdbcType=VARCHAR}


		and TYPE = #{type,jdbcType=VARCHAR}

SELECT/** 综合:统计总记录数 **/ COUNT(*) FROM BSE.T_BAS_SERVICE_BIGZONE BIGZONE
ACTIVE = 'Y'
and REGION_CODE like CONCAT(CONCAT('%',#{regionCode,jdbcType=VARCHAR}),'%')
and REGION_NAME like CONCAT(CONCAT('%',#{regionName,jdbcType=VARCHAR}),'%')
and MANAGEMENT = #{management,jdbcType=VARCHAR}
and TYPE = #{type,jdbcType=VARCHAR}
