mapper.select:

	  SELECT /** 综合:根据条件List查询中间表数据 **/



    	FROM BSE.T_BAS_ORG_MIDDLETABLE A 



			A.ID=#{id,jdbcType=VARCHAR}


			AND A.CODE=#{code,jdbcType=VARCHAR}


			AND A.NAME=#{name,jdbcType=VARCHAR}


			AND A.SENDSTATUS IN 

				#{conditionSendStatus,jdbcType=VARCHAR}



			AND A.UPDATETIME=#{conditionUpdateTime,jdbcType=timestamp}
