mapper.select:
	 select * from (
	 select * from pda.t_pda_asyncmsg where (asyncstatus=0 or (
	    asyncstatus=3 
    	and synccount < 10
    	and syncTime < sysdate-1/24/60*3*synccount
    	)) and opertype in (select OPER_TYPE from pda.T_PDA_JOB_OPER_TYPE where value='1')
    )
     where rownum <= #{count,jdbcType=NUMERIC}
