mapper.select:
SELECT ID as id, CODETYPE as codeType, CODENO as codeNo, CODEVALUE as codeValue, SEQ as seq, OPERFLAG as operFlag FROM T_BASE_CODE WHERE OPERFLAG = '1'
AND UPDTIME > #{startTime,jdbcType=TIMESTAMP}
AND UPDTIME <= #{endTime,jdbcType=TIMESTAMP}


SELECT 
	ID as id
FROM T_BASE_CODE
WHERE OPERFLAG = '3'
AND UPDTIME > #{startTime,jdbcType=TIMESTAMP}
AND UPDTIME <= #{endTime,jdbcType=TIMESTAMP}


SELECT COUNT(*) FROM T_BASE_CODE 


 WHERE UPDTIME >= #{startTime,jdbcType=TIMESTAMP}


 AND UPDTIME < #{endTime,jdbcType=TIMESTAMP}
