mapper.select:
select pn.id as id, pn.code as code, pn.name as name, pn.operflag as operflag, pn.nation_code as nationCode, pn.prov_code as provCode, pn.city_code as cityCode, pn.county_code as countyCode, pn.begin_time as beginTime, pn.end_time as endTime, pn.type as type from t_base_express_eff_region pn WHERE operflag = '1'
AND UPDTIME > #{startTime,jdbcType=TIMESTAMP}
AND UPDTIME <= #{endTime,jdbcType=TIMESTAMP}


SELECT COUNT(*) FROM t_base_express_eff_region	



	 AND UPDTIME > #{startTime,jdbcType=TIMESTAMP}


	 AND UPDTIME <= #{endTime,jdbcType=TIMESTAMP}
