mapper.select:
SELECT ID as id, DEPTCODE as deptCode, STORENUMBER as storeNumber, STORELOCTYPE as storeLocType, ASTERISK_CODE as asterisk_code, OPERFLAG as operFlag, LADINGSTATCODE as ladingStatCode, GOODSTYPE as goodstype FROM T_BASE_STORE WHERE OPERFLAG = '1'
AND UPDTIME > #{startTime,jdbcType=TIMESTAMP}
AND UPDTIME <= #{endTime,jdbcType=TIMESTAMP}


SELECT 
	ID as id
FROM T_BASE_STORE
WHERE OPERFLAG = '3'
AND UPDTIME > #{startTime,jdbcType=TIMESTAMP}
AND UPDTIME <= #{endTime,jdbcType=TIMESTAMP}


SELECT COUNT(*) FROM T_BASE_STORE 


 WHERE UPDTIME >= #{startTime,jdbcType=TIMESTAMP}


 AND UPDTIME < #{endTime,jdbcType=TIMESTAMP}
