select sales.code as code, sales.name as name, sales.Pickup_Area_Desc as pickupAreaDesc, /**自提描述**/ sales.DELIVERY_AREA_DESC as deliveryAreaDesc, /**派送描述**/ sales.Pickup_Self as chbPickupTwo,/**可自提**/ sales.Delivery as chbDeliverTwo,/**可派送**/ sales.CAN_PAY_SERVICE_FEE as chbPayServicefee,/**是否可开装卸费**/ sales.CAN_RETURN_SIGN_BILL as chbReturnBill,/**是否可返回签单**/ sales.CAN_CASH_ON_DELIVERY as chbArrivePayment, /**是否可货到付款**/ sales.CAN_AGENT_COLLECTED as chbCod,/**是否可代收货款**/ sales.PICKUP_AREA_IS_EXPAND as pickUpAreaIsExpand,/**自提区域扩展**/ sales.DELIVERY_AREA_IS_EXPAND as deliveryAreaIsExpand,/**自提区域扩展**/ org.org_simple_name as targetOrgName, /**简称**/ org.Dep_Telephone as phone,/**部门联系电话**/ org.ADDRESS as branchAddress, /**地址**/ org.prov_code as province, /**省**/ org.city_code as city /**市**/ from bse.t_bas_sales_department sales left join bse.t_bas_org org on sales.code = org.code
and sales.NAME like CONCAT(CONCAT('%',#{name,jdbcType=VARCHAR}),'%')
and sales.active = #{active,jdbcType=VARCHAR} and org.active = #{active,jdbcType=VARCHAR}
and sales.Pickup_Self = #{pickupSelf,jdbcType=VARCHAR}
and sales.Delivery = #{delivery,jdbcType=VARCHAR}
and sales.OPENING_DATE <= #{openingDate,jdbcType=TIMESTAMP}

SELECT T.CODE AS CODE,O.NAME AS NAME,T.Single_Piece_Limitkg as Single_Piece_Limitkg, T.Single_Piece_Limitvol as Single_Piece_Limitvol, T.Single_Bill_Limitkg as Single_Bill_Limitkg, T.Single_Bill_Limitvol as Single_Bill_Limitvol, T.PICKUP_SELF AS PICKUP_SELF,T.DELIVERY DELIVERY, T.ARRIVE as ARRIVE, O.ORG_SIMPLE_NAME AS TRANSFER_CENTER, O.CITY_CODE AS PICKUP_AREA_DESC, T.CAN_AGENT_COLLECTED AS CAN_AGENT_COLLECTED, T.CAN_CASH_ON_DELIVERY as CAN_CASH_ON_DELIVERY, T.CANCEL_ARRIVAL_DATE AS CANCEL_ARRIVAL_DATE, T.TRANSFER_GOOD_DEPT AS TRANSFER_GOOD_DEPT, T.CAN_RETURN_SIGN_BILL AS CAN_RETURN_SIGN_BILL,
T.CAN_EXPRESS_RETURN_SIGN_BILL AS CAN_EXPRESS_RETURN_SIGN_BILL, T.CAN_EXPRESS_PICKUP_TO_DOOR AS CAN_EXPRESS_PICKUP_TO_DOOR, T.CAN_EXPRESS_DELIVERY AS CAN_EXPRESS_DELIVERY, T.CAN_EXPRESS_PICKUP_SELF AS CAN_EXPRESS_PICKUP_SELF, T.EXPRESS_PICKUP_AREA_IS_EXP AS EXPRESS_PICKUP_AREA_IS_EXP, T.EXPRESS_DELIVERY_AREA_IS_EXP AS EXPRESS_DELIVERY_AREA_IS_EXP
from BSE.T_BAS_SALES_DEPARTMENT T, BSE.T_BAS_PRO_SALESDEPT P, BSE.T_BAS_ORG O
and T.CODE = O.CODE and T.CODE = P.SALES_DEPT_CODE and T.ACTIVE = #{active,jdbcType=CHAR} and P.ACTIVE = #{active,jdbcType=CHAR} and O.ACTIVE = #{active,jdbcType=CHAR}
and O.ORG_SIMPLE_NAME like CONCAT(CONCAT('%',#{orgSimpleName,jdbcType=VARCHAR}),'%')
and O.NAME like CONCAT(CONCAT('%',#{pickUpPoint,jdbcType=VARCHAR}),'%')
and T.DELIVERY = #{pickUpDoor,jdbcType=CHAR}
and T.PICKUP_SELF = #{pickUpSelf,jdbcType=CHAR}
and T.ARRIVE = #{arrive,jdbcType=CHAR}
and P.PRODUCT_CODE = #{transType,jdbcType=VARCHAR}
and P.SALES_TYPE = #{salesType,jdbcType=VARCHAR}
and T.CODE <> #{receiveOrgCode,jdbcType=VARCHAR}
and t.OPENING_DATE <= #{curDate,jdbcType=TIMESTAMP}
and T.LEAVE = #{leave,jdbcType=CHAR}
and ( O.EXPRESS_SALES_DEPARTMENT != 'Y' OR O.EXPRESS_SALES_DEPARTMENT IS NULL )

