mapper.update:
update PKP.T_SRV_WAYBILL_OFFLINE
set
PRINT_TIMES = #{printTimes,jdbcType=DECIMAL}
where WAYBILL_NO= #{waybillNo,jdbcType=VARCHAR}

mapper.select:
    select 

	        from PKP.T_SRV_WAYBILL_OFFLINE p

	            where  p.BILL_TIME >=  #{billStartTime} and p.BILL_TIME <= #{billEndTime}


	            and  p.CREATE_TIME >=  #{createStartTime} and p.CREATE_TIME <= #{createEndTime}


				and p.RECEIVE_ORG_CODE = #{waybillOfflineEntity.receiveOrgCode,jdbcType=VARCHAR}


				and p.product_code = #{waybillOfflineEntity.productCode,jdbcType=VARCHAR}


				and p.active = #{waybillOfflineEntity.active,jdbcType=VARCHAR}


				and p.create_user_code = #{waybillOfflineEntity.createUserCode,jdbcType=VARCHAR}


				and p.create_org_code = #{waybillOfflineEntity.createOrgCode,jdbcType=VARCHAR}


				and p.WAYBILL_NO = #{mixNo,jdbcType=VARCHAR} or p.ORDER_NO = #{mixNo,jdbcType=VARCHAR}

	                order by p.BILL_TIME asc

select 

      from PKP.T_SRV_WAYBILL_OFFLINE p

      where p.WAYBILL_NO = #{mixNo,jdbcType=VARCHAR} or p.ORDER_NO = #{mixNo,jdbcType=VARCHAR}
