mapper.select:
  select 

        from PKP.T_SRV_WAYBILL_PENDING p where 1=1 


            and p.BILL_TIME >=  #{billStartTime} 





	          and p.BILL_TIME <= #{billEndTime}




		            and  p.CREATE_TIME >=  #{createStartTime} 




		            and p.CREATE_TIME <= #{createEndTime}



			and p.receive_org_code = #{waybillPending.receiveOrgCode,jdbcType=VARCHAR}


			and p.product_code = #{waybillPending.productCode,jdbcType=VARCHAR}

	and p.product_code != 'PACKAGE'

			and p.pending_type = #{waybillPending.pendingType,jdbcType=VARCHAR}


			and p.create_user_code = #{waybillPending.createUserCode,jdbcType=VARCHAR}


			and p.create_org_code = #{waybillPending.createOrgCode,jdbcType=VARCHAR}


			and p.active = #{waybillPending.active,jdbcType=VARCHAR}


			and p.WAYBILL_NO = #{mixNo,jdbcType=VARCHAR} 


			and  p.ORDER_NO = #{orderNo,jdbcType=VARCHAR}

                order by p.BILL_TIME asc

  select 

        from PKP.T_SRV_WAYBILL_PENDING p where 1=1 


            and p.BILL_TIME >=  #{billStartTime} 





	          and p.BILL_TIME <= #{billEndTime}




		            and  p.CREATE_TIME >=  #{createStartTime} 




		            and p.CREATE_TIME <= #{createEndTime}



			and p.receive_org_code = #{waybillPending.receiveOrgCode,jdbcType=VARCHAR}


			and p.product_code = #{waybillPending.productCode,jdbcType=VARCHAR} 


	 and p.product_code IN('PACKAGE','RCP','EPEP')

			and p.pending_type = #{waybillPending.pendingType,jdbcType=VARCHAR}


			and p.create_user_code = #{waybillPending.createUserCode,jdbcType=VARCHAR}


			and p.create_org_code = #{waybillPending.createOrgCode,jdbcType=VARCHAR}


			and p.active = #{waybillPending.active,jdbcType=VARCHAR}


			and p.WAYBILL_NO = #{mixNo,jdbcType=VARCHAR} 


			and  p.ORDER_NO = #{orderNo,jdbcType=VARCHAR}

                order by p.BILL_TIME asc

mapper.update:
  	update PKP.T_SRV_WAYBILL_PENDING p
	    set 

	       p.ACTIVE = #{active,jdbcType=VARCHAR}

   		 	where p.ID = #{id,jdbcType=VARCHAR}

  	update PKP.T_SRV_WAYBILL_PENDING p
	    set 

	       p.ACTIVE = #{active,jdbcType=VARCHAR}

   		 	where p.WAYBILL_NO = #{waybillNo,jdbcType=VARCHAR}

mapper.select:
SELECT

FROM PKP.T_SRV_WAYBILL_PENDING P
WHERE P.ACTIVE = #{active,jdbcType=VARCHAR}

	AND P.WAYBILL_NO = #{waybillNo,jdbcType=VARCHAR}


	AND P.ORDER_NO = #{orderNo,jdbcType=VARCHAR}
