mapper.select:
select

from PKP.T_SRV_WAYBILL
where ID = #{id,jdbcType=VARCHAR}

select * from (
select	/*模块：接送货-运单查询-根据运单号和订单号查询运单基本信息*/

from PKP.T_SRV_WAYBILL
where 1 = 1

		and ACTIVE = #{active,jdbcType=CHAR}




		and WAYBILL_NO = #{waybillNo,jdbcType=VARCHAR}


		and ORDER_NO = #{orderNo,jdbcType=VARCHAR}

) where  ROWNUM = 1

select k.WAYBILL_NO, k.GOODS_QTY_TOTAL, k.GOODS_WEIGHT_TOTAL, k.PRODUCT_CODE from PKP.T_SRV_WAYBILL k
ACTIVE = #{active,jdbcType=CHAR}
and WAYBILL_NO = #{waybillNo,jdbcType=VARCHAR}

select * from (
select	/*模块：接送货-变更运单-根据运单号查询运单基本信息*/

from PKP.T_SRV_WAYBILL
where 1 = 1

		and ACTIVE = #{active,jdbcType=CHAR}




		and WAYBILL_NO = #{waybillNo,jdbcType=VARCHAR}


order
by CREATE_TIME desc
) where  ROWNUM = 1

SELECT /*模块：接送货-运单查询-查询客户所发第一票货*/ W.WAYBILL_NO,W.ID,W.PAID_METHOD,W.TO_PAY_AMOUNT/100 AS TO_PAY_AMOUNT, W.CUSTOMER_PICKUP_ORG_CODE,W.CURRENCY_CODE,W.PRODUCT_CODE,W.IS_WHOLE_VEHICLE, W.RECEIVE_METHOD,W.TOTAL_FEE/100 AS TOTAL_FEE, W.INSURANCE_AMOUNT/100 AS INSURANCE_AMOUNT, W.COD_AMOUNT/100 AS COD_AMOUNT,W.GOODS_QTY_TOTAL,W.LAST_LOAD_ORG_CODE,W.ORDER_NO, W.DELIVERY_CUSTOMER_MOBILEPHONE,W.RECEIVE_CUSTOMER_CONTACT, W.RECEIVE_CUSTOMER_MOBILEPHONE,W.DELIVERY_CUSTOMER_CONTACT, W.RECEIVE_ORG_CODE,W.WAYBILL_NO,W.FREIGHT_METHOD,W.BILL_TIME
FROM PKP.T_SRV_WAYBILL W LEFT JOIN PKP.T_SRV_ACTUAL_FREIGHT F ON F.WAYBILL_NO = W.WAYBILL_NO WHERE ROWNUM = 1
AND W.ACTIVE = #{active,jdbcType=CHAR}
AND F.STATUS NOT IN ('ABORTED','OBSOLETE')
AND W.RECEIVE_ORG_CODE = #{receiveOrgCode,jdbcType=VARCHAR}
AND W.DELIVERY_CUSTOMER_CODE = #{deliveryCustomerCode,jdbcType=VARCHAR}
AND W.DELIVERY_CUSTOMER_MOBILEPHONE = #{deliveryCustomerMobilephone,jdbcType=VARCHAR}
AND W.DELIVERY_CUSTOMER_PHONE = #{deliveryCustomerPhone,jdbcType=VARCHAR}
AND (
#{billTime,jdbcType=TIMESTAMP} >= (TRUNC(#{billTime,jdbcType=TIMESTAMP}) + 8 / 24) AND (W.BILL_TIME >= TRUNC(#{billTime,jdbcType=TIMESTAMP}) + 8 / 24 AND W.BILL_TIME < TRUNC(#{billTime,jdbcType=TIMESTAMP}) + 1 + 8 / 24)
OR (#{billTime,jdbcType=TIMESTAMP} < (TRUNC(#{billTime,jdbcType=TIMESTAMP}) + 8 / 24) AND (W.BILL_TIME >= TRUNC(#{billTime,jdbcType=TIMESTAMP})-1 + 8 / 24 AND W.BILL_TIME < TRUNC(#{billTime,jdbcType=TIMESTAMP}) + 8 / 24)) )
AND W.PRODUCT_CODE <> #{productCode,jdbcType=VARCHAR}
AND W.PRODUCT_CODE not in ('PACKAGE','RCP','EPEP')
ORDER BY W.BILL_TIME ASC

select	/*模块：接送货-运单查询-根据运单号查询不含PDA待补录的运单基本信息 */

from PKP.T_SRV_WAYBILL


	ROWNUM = 1

		and ACTIVE = #{active,jdbcType=CHAR}


		and WAYBILL_NO = #{waybillNo,jdbcType=VARCHAR}


		and PENDING_TYPE   <>  
		#{waybillStatus,jdbcType=VARCHAR}

select	/*模块：接送货-装车-根据运单号、流水号查询货签基本信息*/
ID
from PKP.T_SRV_LABELED_GOOD L


		ACTIVE = #{active,jdbcType=CHAR}


		AND WAYBILL_NO = #{waybillNo,jdbcType=VARCHAR}


		AND SERIAL_NO = #{serialNo,jdbcType=VARCHAR}

select	/*模块：接送货-运单-通过运单编号集合查询运单*/

from PKP.T_SRV_WAYBILL

	and ACTIVE = #{active,jdbcType=CHAR}

		and WAYBILL_NO in

			#{item}

select /*模块：接送货-运单-查询运单*/ P.ID, P.WAYBILL_NO, ORDER_NO, ORDER_CHANNEL,ORDER_PAID_METHOD, DELIVERY_CUSTOMER_ID, DELIVERY_CUSTOMER_CODE, DELIVERY_CUSTOMER_NAME, DELIVERY_CUSTOMER_MOBILEPHONE, DELIVERY_CUSTOMER_PHONE, DELIVERY_CUSTOMER_CONTACT, DELIVERY_CUSTOMER_NATION_CODE, DELIVERY_CUSTOMER_PROV_CODE, DELIVERY_CUSTOMER_CITY_CODE, DELIVERY_CUSTOMER_DIST_CODE, DELIVERY_CUSTOMER_ADDRESS, RECEIVE_CUSTOMER_ID, RECEIVE_CUSTOMER_CODE, RECEIVE_CUSTOMER_NAME, RECEIVE_CUSTOMER_MOBILEPHONE, RECEIVE_CUSTOMER_PHONE, RECEIVE_CUSTOMER_CONTACT, RECEIVE_CUSTOMER_NATION_CODE, RECEIVE_CUSTOMER_PROV_CODE, RECEIVE_CUSTOMER_CITY_CODE, RECEIVE_CUSTOMER_DIST_CODE, RECEIVE_CUSTOMER_ADDRESS, RECEIVE_ORG_CODE, PRODUCT_ID, PRODUCT_CODE, RECEIVE_METHOD, CUSTOMER_PICKUP_ORG_CODE, LOAD_METHOD, TARGET_ORG_CODE, PICKUP_TO_DOOR, DRIVER_CODE, PICKUP_CENTRALIZED, LOAD_LINE_CODE, LOAD_ORG_CODE, LAST_LOAD_ORG_CODE, PRE_DEPARTURE_TIME, PRE_CUSTOMER_PICKUP_TIME, CAR_DIRECT_DELIVERY, P.GOODS_NAME, GOODS_QTY_TOTAL, GOODS_WEIGHT_TOTAL, GOODS_VOLUME_TOTAL, GOODS_SIZE, P.GOODS_TYPE_CODE, PRECIOUS_GOODS, SPECIAL_SHAPED_GOODS, OUTER_NOTES, INNER_NOTES, GOODS_PACKAGE, INSURANCE_AMOUNT/100 as INSURANCE_AMOUNT, P.COD_AMOUNT/100 as COD_AMOUNT, COD_RATE, COD_FEE/100 as COD_FEE, REFUND_TYPE, RETURN_BILL_TYPE, SECRET_PREPAID, TO_PAY_AMOUNT/100 as TO_PAY_AMOUNT, PRE_PAY_AMOUNT/100 as PRE_PAY_AMOUNT, DELIVERY_GOODS_FEE/100 as DELIVERY_GOODS_FEE, OTHER_FEE/100 as OTHER_FEE, PACKAGE_FEE/100 as PACKAGE_FEE, PROMOTIONS_FEE/100 as PROMOTIONS_FEE, BILLING_TYPE, TRANSPORT_FEE/100 as TRANSPORT_FEE, VALUE_ADD_FEE/100 as VALUE_ADD_FEE, PAID_METHOD, ARRIVE_TYPE, ACTIVE, FORBIDDEN_LINE, FREIGHT_METHOD, FLIGHT_SHIFT, TOTAL_FEE/100 as TOTAL_FEE, PROMOTIONS_CODE, P.CREATE_TIME, P.MODIFY_TIME, BILL_TIME, CREATE_USER_CODE, MODIFY_USER_CODE, CREATE_ORG_CODE, MODIFY_ORG_CODE, REF_ID, REF_CODE, CURRENCY_CODE, IS_WHOLE_VEHICLE, WHOLE_VEHICLE_APPFEE/100 as WHOLE_VEHICLE_APPFEE, WHOLE_VEHICLE_ACTUALFEE/100 as WHOLE_VEHICLE_ACTUALFEE, ACCOUNT_NAME, ACCOUNT_CODE, ACCOUNT_BANK, BILL_WEIGHT, PICKUP_FEE/100 as PICKUP_FEE, SERVICE_FEE/100 as SERVICE_FEE, PRE_ARRIVE_TIME, TRANSPORT_TYPE, ADD_TIME, INSURANCE_RATE, INSURANCE_FEE/100 as INSURANCE_FEE, UNIT_PRICE/100 AS UNIT_PRICE,CONTACT_ADDRESS_ID, FLIGHT_NUMBER_TYPE, COLLECTION_DEPARTMENT, TRANSPORTATION_REMARK,IS_PASS_OWN_DEPARTMENT, PAPER_NUM,WOOD_NUM,FIBRE_NUM,SALVER_NUM,MEMBRANE_NUM,OTHER_PACKAGE,DELIVERY_CUSTOMER_CONTACT_ID,RECEIVE_CUSTOMER_CONTACT_ID, PENDING_TYPE,LICENSE_PLATE_NUM,ORDER_VEHICLE_NUM, CREATE_USER_NAME,CREATE_USER_DEPT_NAME ,RECEIVE_ORG_NAME,CUSTOMER_PICKUP_ORG_NAME,LOAD_ORG_NAME,LAST_LOAD_ORG_NAME, TRANSFER_START_ORG_CODE, TRANSFER_START_ORG_NAME,KILOMETER,IS_INIT,IS_ECONOMY_GOODS,ECONOMY_GOODS_TYPE,DELIVERY_BIG_CUSTOMER,RECEIVE_BIG_CUSTOMER, T.WAYBILL_TYPE,P.IS_ECS from PKP.T_SRV_WAYBILL p LEFT JOIN PKP.T_SRV_ACTUAL_FREIGHT T on(P.WAYBILL_NO = T.WAYBILL_NO)
(p.is_ecs != 'Y' or p.is_ecs is null) and
P.PENDING_TYPE NOT IN ( 'RFC_ACTIVE','RFC_PENDING','PDA_PENDING') AND p.active = #{waybillEntity.active,jdbcType=VARCHAR}
and p.BILL_TIME >= #{billStartTime,jdbcType=TIMESTAMP}
AND p.BILL_TIME <= #{billEndTime,jdbcType=TIMESTAMP}
and p.CREATE_TIME >= #{createStartTime,jdbcType=TIMESTAMP}
and p.CREATE_TIME <= #{createEndTime,jdbcType=TIMESTAMP}
and p.receive_org_code = #{waybillEntity.receiveOrgCode,jdbcType=VARCHAR}
and p.receive_org_code in #{receiveOrgCode, jdbcType=VARCHAR}
and p.product_code = #{waybillEntity.productCode,jdbcType=VARCHAR}
AND p.product_code IN ('PACKAGE','RCP','EPEP','DEAP')
and p.pending_type = #{waybillEntity.pendingType,jdbcType=VARCHAR}
and p.create_user_code = #{waybillEntity.createUserCode,jdbcType=VARCHAR}
and p.create_org_code = #{waybillEntity.createOrgCode,jdbcType=VARCHAR}
and p.create_org_code in #{createOrgCode, jdbcType=VARCHAR}
and p.WAYBILL_NO = #{mixNo,jdbcType=CHAR}
and p.ORDER_NO =#{orderNo,jdbcType=VARCHAR} and p.ORDER_NO is not null
AND T.WAYBILL_TYPE IS NULL
AND T.WAYBILL_TYPE =#{waybillType,jdbcType=VARCHAR}
AND T.IS_BATCH_CREATE_WAYBILL =#{actualFreightEntity.isBatchCreateWaybill,jdbcType=VARCHAR}
order by BILL_TIME asc

