ME, ACCOUNT_CODE, ACCOUNT_BANK, BILL_WEIGHT, PICKUP_FEE/100 as PICKUP_FEE, SERVICE_FEE/100 as SERVICE_FEE, PRE_ARRIVE_TIME, TRANSPORT_TYPE, ADD_TIME, INSURANCE_RATE, INSURANCE_FEE/100 as INSURANCE_FEE, UNIT_PRICE/100 AS UNIT_PRICE,CONTACT_ADDRESS_ID, FLIGHT_NUMBER_TYPE, COLLECTION_DEPARTMENT, TRANSPORTATION_REMARK,IS_PASS_OWN_DEPARTMENT, PAPER_NUM,WOOD_NUM,FIBRE_NUM,SALVER_NUM,MEMBRANE_NUM,OTHER_PACKAGE,DELIVERY_CUSTOMER_CONTACT_ID,RECEIVE_CUSTOMER_CONTACT_ID, PENDING_TYPE,LICENSE_PLATE_NUM,ORDER_VEHICLE_NUM, CREATE_USER_NAME,CREATE_USER_DEPT_NAME ,RECEIVE_ORG_NAME,CUSTOMER_PICKUP_ORG_NAME,LOAD_ORG_NAME,LAST_LOAD_ORG_NAME, TRANSFER_START_ORG_CODE, TRANSFER_START_ORG_NAME,KILOMETER,IS_INIT,IS_ECONOMY_GOODS,ECONOMY_GOODS_TYPE,DELIVERY_BIG_CUSTOMER,RECEIVE_BIG_CUSTOMER, T.WAYBILL_TYPE from PKP.T_SRV_WAYBILL p LEFT JOIN PKP.T_SRV_ACTUAL_FREIGHT T on(P.WAYBILL_NO = T.WAYBILL_NO)
P.PENDING_TYPE NOT IN ( 'RFC_ACTIVE','RFC_PENDING') AND p.active = #{waybillEntity.active,jdbcType=VARCHAR}
and p.BILL_TIME >= #{billStartTime,jdbcType=TIMESTAMP}
AND p.BILL_TIME <= #{billEndTime,jdbcType=TIMESTAMP}
