select /*模块：接送货-运单-查询运单*/

from PKP.T_SRV_WAYBILL p


	P.PENDING_TYPE NOT IN ( 'RFC_ACTIVE','RFC_PENDING','PDA_PENDING')
	AND
	p.active = #{waybillEntity.active,jdbcType=VARCHAR}

		and p.BILL_TIME    >=  
		#{billStartTime,jdbcType=TIMESTAMP}


		AND p.BILL_TIME    <=  
		#{billEndTime,jdbcType=TIMESTAMP}


		and p.CREATE_TIME   >=  
		#{createStartTime,jdbcType=TIMESTAMP}


		and p.CREATE_TIME   <=  
		#{createEndTime,jdbcType=TIMESTAMP}


		and p.receive_org_code =
		#{waybillEntity.receiveOrgCode,jdbcType=VARCHAR}


		and p.receive_org_code in
		 #{receiveOrgCode,
			jdbcType=VARCHAR}



		and p.product_code =
		#{waybillEntity.productCode,jdbcType=VARCHAR}

	 and p.product_code not in ('PACKAGE','RCP','EPEP')

		and p.pending_type =
		#{waybillEntity.pendingType,jdbcType=VARCHAR}


		and p.create_user_code =
		#{waybillEntity.createUserCode,jdbcType=VARCHAR}


		and (p.create_org_code =
		#{waybillEntity.createOrgCode,jdbcType=VARCHAR} or (p.RECEIVE_ORG_CODE = #{waybillEntity.createOrgCode,jdbcType=VARCHAR} AND EXISTS (SELECT 1 FROM PKP.T_SRV_OMS_ORDER IT WHERE IT.WAYBILL_NO = p.WAYBILL_NO)))


		and (p.create_org_code in
		 #{createOrgCode,
			jdbcType=VARCHAR}

		OR (
		p.RECEIVE_ORG_CODE in
		 #{createOrgCode,
			jdbcType=VARCHAR}

		and EXISTS (SELECT 1 FROM PKP.T_SRV_OMS_ORDER IT WHERE IT.WAYBILL_NO = p.WAYBILL_NO)
		))


		and p.WAYBILL_NO = #{mixNo,jdbcType=CHAR}


		and p.ORDER_NO =#{orderNo,jdbcType=VARCHAR} and
		p.ORDER_NO is not null


order by BILL_TIME asc

SELECT ABANDONGOODS.Id id, ABANDONGOODS.Status status, ABANDONGOODS.WAYBILL_NO waybillNo, ABANDONGOODS.Delivery_Customer_Mobilephone deliverCustomerMobile, ABANDONGOODS.Respective_Regional respectiveRegional, ABANDONGOODS.Goods_Name goodsName, ABANDONGOODS.Goods_Weight_Total weight, ABANDONGOODS.Goods_Volume_Total volume, ABANDONGOODS.Delivery_Customer_Contact deliverCustomerContact, ABANDONGOODS.DELIVERY_CUSTOMER_PHONE deliverCostomerPhone, ABANDONGOODS.Receive_Org_Code receiveOrgCode, ABANDONGOODS.RECEIVE_CUSTOMER_CONTACT receiverName, ABANDONGOODS.RECEIVE_CUSTOMER_PHONE receiverTel, ABANDONGOODS.Last_Load_Org_Code lastLoadOrgCode, ABANDONGOODS.Storage_Org_Code storageOrgCode, ABANDONGOODS.Cod_Amount codAmount, ABANDONGOODS.Insurance_Amount insuranceAmount, ABANDONGOODS.Pre_Pay_Amount prePayAmount, ABANDONGOODS.To_Pay_Amount toPayamount, ABANDONGOODS.Abandonedgoods_Type goodsType, ABANDONGOODS.Abandonedgoods_Status abandoneGoodsStatus, TO_CHAR(ABANDONGOODS.PREABANDONEDGOODS_TIME,'yyyy-mm-dd hh24:mi:ss') treatTime, ABANDONGOODS.CREATE_USER_NAME preTreatPerson, ABANDONGOODS.Storage_Day storageDay, ABANDONGOODS.Notes notes, ABANDONGOODS.Goods_Qty_Total pieces FROM PKP.T_SRV_ABANDON_GOODS_IMPORT ABANDONGOODS
ABANDONGOODS.WAYBILL_NO = #{waybillNo,jdbcType=VARCHAR} AND ABANDONGOODS.ACTIVE = #{active,jdbcType=VARCHAR}
ABANDONGOODS.DELIVERY_CUSTOMER_CONTACT = #{shipper,jdbcType=VARCHAR}
AND ABANDONGOODS.ABANDONEDGOODS_TYPE = #{exceptionType,jdbcType=VARCHAR}
AND ABANDONGOODS.CREATE_USER_NAME = #{preTreatPerson,jdbcType=VARCHAR}
AND ABANDONGOODS.PREABANDONEDGOODS_TIME BETWEEN #{beginDate,jdbcType=TIMESTAMP} AND #{endDate,jdbcType=TIMESTAMP} AND ABANDONGOODS.ACTIVE = #{active,jdbcType=VARCHAR}

select /*模块：接送货-运单-查询运单明细*/ waybill.waybill_no waybillNo, waybill.Transport_Type tranType, waybill.delivery_customer_name sender, waybill.delivery_customer_phone senderPhone, waybill.delivery_customer_mobilephone senderMobile, waybill.receive_org_code departure, waybill.delivery_customer_address senderAddress, waybill.receive_customer_name consignee, waybill.receive_customer_phone consigneePhone, waybill.receive_customer_mobilephone consigneeMobile, waybill.target_org_code destination, waybill.receive_customer_address consigneeAddress, waybill.goods_name goodName, waybill.pre_arrive_time predictArriveTime, waybill.goods_qty_total pieces, waybill.goods_weight_total weight, waybill.goods_volume_total cubage, waybill.total_fee totalCharge, waybill.paid_method payment, waybill.pre_pay_amount preCharge, waybill.to_pay_amount arriveCharge, waybill.refund_type refundType, waybill.cod_amount refund, waybill.cod_fee refundFee, waybill.receive_method deliveryType, waybill.pickup_fee consignCharge, waybill.delivery_goods_fee deliveryCharge, waybill.service_fee laborRebate, waybill.transport_fee publishCharge, org.name departureDeptName, org.code departureDeptNumber, org.address departureDeptAddr, org.dep_telephone departureDeptPhone, org.dep_fax departureDeptFax, lad.name ladingStationName, lad.code ladingStationNumber, lad.address ladingStationAddr, lad.dep_telephone ladingStationPhone, lad.dep_fax ladingStationFax, (case when signResult.Waybill_No is null then '是' else '否' end) isSigned, signResult.Sign_Situation isNormalSigned, sheet.create_user_name signRecorderId, signResult.Sign_Time signedDate, signResult.Sign_Note signedDesc, waybill.order_no orderNumber, waybill.insurance_amount insuranceValue, waybill.goods_package packing, waybill.active orderState, waybill.inner_notes tranDesc, waybill.delivery_customer_code senderNumber, waybill.receive_customer_code consigneeNumber, waybill.return_bill_type signBackType, waybill.load_method stowageType, waybill.transportation_remark transNotice, waybill.create_time sendTime, org.name receiveDeptName, org.code receiveDeptNumber, waybill.load_org_code stowageDept, waybill.delivery_customer_city_code senderCityCode, deliverCity.name senderCityName, waybill.delivery_customer_prov_code senderProvinceCode, deliverPro.Name senderProvinceName, waybill.receive_customer_city_code consigneeCityCode, receiveCity.Name consigneeCityName, waybill.receive_customer_prov_code consigneeProvinceCode, receivePro.Name consigneeProvinceName, waybill.pickup_to_door isDoorToDoorPick, waybill.return_bill_type signBillBackWay, chargeDetail.Pricing_Entry_Code costType, '' costName, chargeDetail.Amount costMoney from pkp.t_srv_waybill waybill left join pkp.t_Srv_Waybill_Charge_Detail chargeDetail on waybill.waybill_no = chargeDetail.Waybill_No left join bse.t_bas_org org on waybill.receive_org_code = org.code left join bse.t_bas_org lad on waybill.target_org_code = lad.code left join pkp.t_srv_waybill_sign_result signResult on signResult.Waybill_No = waybill.waybill_no left join pkp.t_srv_arrivesheet sheet on sheet.waybill_no = signResult.Waybill_No left join bse.t_Bas_District deliverCity on deliverCity.code = waybill.delivery_customer_city_code left join bse.t_bas_district deliverPro on deliverPro.Code = waybill.delivery_customer_prov_code left join bse.t_Bas_District receiveCity on receiveCity.Code = waybill.receive_customer_city_code left join bse.t_Bas_District receivePro on receivePro.Code = waybill.receive_customer_prov_code where waybill.active = 'Y'
and waybill.waybill_no in
#{item,jdbcType=VARCHAR}

select /*模块：接送货-运单-综合查询:根据不同的查询条件返回运单单号集合*/
w.waybill_no waybillNo from
pkp.t_srv_waybill w

	w.is_ecs IS NULL AND 
	w.active = 'Y'

	 and ROWNUM <= 1000  


		and w.waybill_no >=#{startWaybillNo,jdbcType=VARCHAR}


		and
	w.waybill_no < =#{endWaybillNo,jdbcType=VARCHAR}



		and w.bill_time BETWEEN #{startBillTime,jdbcType=TIMESTAMP}
		and
		#{endBilltime,jdbcType=TIMESTAMP}

			and
			w.receive_org_code=#{receiveOrgCode,jdbcType=VARCHAR}



			and
			w.product_code=#{productCode,jdbcType=VARCHAR}



			and
			w.CUSTOMER_PICKUP_ORG_CODE=#{targetOrgCode,jdbcType=VARCHAR}





			and
			w.delivery_Customer_Contact
			=#{deliveryCustomerContact,jdbcType=VARCHAR}



			and
			w.receive_Customer_Contact
			=#{receiveCustomerContact,jdbcType=VARCHAR}



			and
			w.delivery_Customer_Mobilephone=#{deliveryCustomerMobilephone,jdbcType=VARCHAR}




			and
			w.receive_customer_Mobilephone=#{receiveCustomerMobilephone,jdbcType=VARCHAR}




			and
			w.delivery_Customer_Phone=#{deliveryCustomerPhone,jdbcType=VARCHAR}



			and
			w.receive_customer_phone=#{receiveCustomerPhone,jdbcType=VARCHAR}



order by w.bill_time desc

