		= #{waybillNo,jdbcType=VARCHAR}
      AND ACTIVE = #{conActive,jdbcType=CHAR}
      AND PENDING_TYPE = #{pendingType,jdbcType=VARCHAR}

mapper.sql:
FROM PKP.T_SRV_WAYBILL W
LEFT JOIN PKP.T_SRV_WAYBILL_DIS_DETAIL DIS ON
W.WAYBILL_NO=DIS.WAYBILL_NO
AND DIS.ACTIVE=#{active,jdbcType=VARCHAR}
AND
DIS.TYPE=#{discountType,jdbcType=VARCHAR}
LEFT JOIN
PKP.T_SRV_PRODUCT P ON W.PRODUCT_CODE=P.CODE AND
P.ACTIVE=#{active,jdbcType=VARCHAR}
LEFT JOIN BSE.T_BAS_DISTRICT PROV
ON
W.RECEIVE_CUSTOMER_PROV_CODE=PROV.CODE AND
PROV.ACTIVE=#{active,jdbcType=VARCHAR}
LEFT JOIN BSE.T_BAS_DISTRICT
CITY ON
W.RECEIVE_CUSTOMER_CITY_CODE=CITY.CODE AND
CITY.ACTIVE=#{active,jdbcType=VARCHAR}
LEFT JOIN BSE.T_BAS_DISTRICT
DIST ON
W.RECEIVE_CUSTOMER_DIST_CODE=DIST.CODE AND
DIST.ACTIVE=#{active,jdbcType=VARCHAR}
LEFT JOIN
BSE.T_BAS_MARK_ACTIVITIES MARK ON DIS.ACTIVE_CODE=MARK.CODE AND
MARK.ACTIVE=#{active,jdbcType=VARCHAR}
LEFT JOIN
PKP.T_SRV_ACTUAL_FREIGHT ACT ON W.WAYBILL_NO=ACT.WAYBILL_NO
WHERE
W.ACTIVE=#{active,jdbcType=VARCHAR}

	 AND W.BILL_TIME  >=  #{startDate,jdbcType=TIMESTAMP}


	 AND W.BILL_TIME  <  #{endDate,jdbcType=TIMESTAMP}


	AND W.DELIVERY_CUSTOMER_CODE =
	#{deliveryCustomerCode,jdbcType=VARCHAR}


	 AND ACT.STATUS  <>  #{obsolete,jdbcType=VARCHAR}


	 AND ACT.STATUS  <>  #{aborted,jdbcType=VARCHAR}

 ORDER BY W.BILL_TIME

mapper.select:
SELECT /*模块：接送货-运单查询-查询该发货客户当天是否收取过接货费*/ COUNT(1) FROM PKP.T_SRV_WAYBILL W LEFT JOIN PKP.T_SRV_ACTUAL_FREIGHT F ON F.WAYBILL_NO = W.WAYBILL_NO WHERE
W.ACTIVE = #{active,jdbcType=CHAR}
AND F.STATUS NOT IN ('ABORTED','OBSOLETE')
AND W.RECEIVE_ORG_CODE = #{receiveOrgCode,jdbcType=VARCHAR}
AND W.DELIVERY_CUSTOMER_CODE = #{deliveryCustomerCode,jdbcType=VARCHAR}
AND W.DELIVERY_CUSTOMER_MOBILEPHONE = #{deliveryCustomerMobilephone,jdbcType=VARCHAR}
AND W.DELIVERY_CUSTOMER_PHONE = #{deliveryCustomerPhone,jdbcType=VARCHAR}
AND (
#{billTime,jdbcType=TIMESTAMP} >= (TRUNC(#{billTime,jdbcType=TIMESTAMP}) + 8 / 24) AND (W.BILL_TIME >= TRUNC(#{billTime,jdbcType=TIMESTAMP}) + 8 / 24 AND W.BILL_TIME < TRUNC(#{billTime,jdbcType=TIMESTAMP}) + 1 + 8 / 24)
OR (#{billTime,jdbcType=TIMESTAMP} < (TRUNC(#{billTime,jdbcType=TIMESTAMP}) + 8 / 24) AND (W.BILL_TIME >= TRUNC(#{billTime,jdbcType=TIMESTAMP})-1 + 8 / 24 AND W.BILL_TIME < TRUNC(#{billTime,jdbcType=TIMESTAMP}) + 8 / 24)) )
AND W.Pickup_To_Door = #{pickupToDoor,jdbcType=VARCHAR}
AND W.WAYBILL_NO <> #{waybillNo,jdbcType=VARCHAR}
AND W.PRODUCT_CODE <> #{productCode,jdbcType=VARCHAR}
AND W.PRODUCT_CODE NOT IN ('PACKAGE','RCP','EPEP')

mapper.sql:
from pkp.t_srv_waybill waybill left join bse.t_bas_org depart on waybill.receive_org_code = depart.code and depart.active=#{active,jdbcType=VARCHAR} left join pkp.t_srv_waybill_sign_result signre on signre.waybill_no =
waybill.waybill_no and signre.active=#{active,jdbcType=VARCHAR} left join bse.t_bas_outer_branch agen on waybill.customer_pickup_org_code= agen.agent_dept_code and agen.active=#{active,jdbcType=VARCHAR} inner join pkp.t_srv_actual_freight f on f.waybill_no=waybill.waybill_no left join bse.t_bas_org lading on waybill.customer_pickup_org_code = lading.code and lading.active=#{active,jdbcType=VARCHAR} left join bse.t_bas_district depa on waybill.DELIVERY_CUSTOMER_CITY_CODE=depa.code and depa.active=#{active,jdbcType=VARCHAR} left join bse.t_bas_district dest on waybill.RECEIVE_CUSTOMER_CITY_CODE=dest.code and dest.active=#{active,jdbcType=VARCHAR} where waybill.active = #{active,jdbcType=VARCHAR}
and (waybill.delivery_customer_mobilephone =#{deliverMobile,jdbcType=VARCHAR} or waybill.receive_customer_mobilephone =#{receiveMobile,jdbcType=VARCHAR})
and waybill.delivery_customer_mobilephone =#{deliverMobile,jdbcType=VARCHAR}
and waybill.receive_customer_mobilephone =#{receiveMobile,jdbcType=VARCHAR}
and waybill.create_time between #{startDate,jdbcType=TIMESTAMP} and #{endDate,jdbcType=TIMESTAMP}
and (
waybill.receive_customer_contact like '%${keyWords}%' or waybill.receive_customer_phone like '%${keyWords}%' or waybill.receive_customer_mobilephone like '%${keyWords}%' or dest.name like '%${keyWords}%'
waybill.delivery_customer_contact like '%${keyWords}%' or waybill.delivery_customer_phone like '%${keyWords}%' or waybill.delivery_customer_mobilephone like '%${keyWords}%' or depa.name like '%${keyWords}%'
waybill.receive_customer_contact like '%${keyWords}%' or waybill.delivery_customer_contact like '%${keyWords}%' or waybill.delivery_customer_phone like '%${keyWords}%' or waybill.delivery_customer_mobilephone like '%${keyWords}%' or waybill.receive_customer_phone like '%${keyWords}%' or waybill.receive_customer_mobilephone like '%${keyWords}%' or dest.name like '%${keyWords}%' or depa.name like '%${keyWords}%'
or waybill.waybill_no like '%${keyWords}%' or waybill.order_no like '%${keyWords}%' )
and waybill.create_time between #{startDate,jdbcType=TIMESTAMP} and #{endDate,jdbcType=TIMESTAMP}
and (waybill.delivery_customer_mobilephone =#{deliverMobile,jdbcType=VARCHAR} or waybill.receive_customer_mobilephone =#{receiveMobile,jdbcType=VARCHAR})
and waybill.delivery_customer_mobilephone =#{deliverMobile,jdbcType=VARCHAR}
and waybill.receive_customer_mobilephone =#{receiveMobile,jdbcType=VARCHAR}
order by waybill.bill_time desc

mapper.select:
select phone_num ,count(waybill_no) count from
(
	select distinct * from
	(
	 	(select DELIVERY_CUSTOMER_MOBILEPHONE as phone_num,
waybill_no from
pkp.t_srv_waybill
			where DELIVERY_CUSTOMER_MOBILEPHONE in  
			(
				${mobilePhones}		
			)
			and BILL_TIME >=#{startDate,jdbcType=TIMESTAMP}
			and BILL_TIME <=#{endDate,jdbcType=TIMESTAMP}
			AND ACTIVE='Y'
		)union
	 	(select DELIVERY_CUSTOMER_PHONE as phone_num,
 			waybill_no from pkp.t_srv_waybill
			where DELIVERY_CUSTOMER_PHONE in  
			(
				${phones}		
			)
			and BILL_TIME >=#{startDate,jdbcType=TIMESTAMP}
			and BILL_TIME <=#{endDate,jdbcType=TIMESTAMP}
			AND ACTIVE='Y'
		)

		union
		(select RECEIVE_CUSTOMER_MOBILEPHONE as phone_num,
	waybill_no
	from pkp.t_srv_waybill
			where RECEIVE_CUSTOMER_MOBILEPHONE in  
			(
				${mobilePhones}		
			)
			and BILL_TIME >=#{startDate,jdbcType=TIMESTAMP}
			and BILL_TIME <=#{endDate,jdbcType=TIMESTAMP}
			AND ACTIVE='Y'
		)union
	 	(select RECEIVE_CUSTOMER_PHONE as phone_num,
 			waybill_no from pkp.t_srv_waybill
			where RECEIVE_CUSTOMER_PHONE in  
			(
				${phones}		
			)
			and BILL_TIME >=#{startDate,jdbcType=TIMESTAMP}
			and BILL_TIME <=#{endDate,jdbcType=TIMESTAMP}
			AND ACTIVE='Y'
		)

	)
)
group by phone_num

mapper.sql:
from pkp.t_srv_waybill waybill /*模块：接送货-电子运单查询-官网查询打印已激活电子运单数量*/ inner join pkp.t_srv_actual_freight freight on freight.waybill_no=waybill.waybill_no inner join pkp.t_srv_dispatch_order dispatchOrder on dispatchOrder.order_no = waybill.order_no inner join bse.t_bas_org org on org.code = waybill.CUSTOMER_PICKUP_ORG_CODE where waybill.active = #{active,jdbcType=VARCHAR} and org.active ='Y' and freight.waybill_type= #{waybillType,jdbcType=VARCHAR}
and ( waybill.delivery_customer_contact_id = #{linkmanId,jdbcType=VARCHAR}
or freight.CHANNELCUSTID = #{userName,jdbcType=VARCHAR}
)
and waybill.waybill_no =#{waybillNO,jdbcType=VARCHAR}
and waybill.order_no =#{orderNo,jdbcType=VARCHAR}
and waybill.DELIVERY_CUSTOMER_CONTACT like CONCAT(#{deliveryCustomerName,jdbcType=VARCHAR},'%')
and waybill.RECEIVE_CUSTOMER_NAME like CONCAT(#{receiveCustomerName,jdbcType=VARCHAR},'%')
and waybill.goods_name like CONCAT(#{goodsName,jdbcType=VARCHAR},'%')
and dispatchOrder.ORDER_TIME between #{startDate,jdbcType=TIMESTAMP} and #{endDate,jdbcType=TIMESTAMP}
order by dispatchOrder.ORDER_TIME desc

