mapper.select:
select /*模块：接送货-运单-根据客户编码、开始时间、结束时间返回运单详细信息*/ w.delivery_customer_code , w.waybill_no , w.create_time , w.goods_name, w.goods_package, w.goods_qty_total , w.goods_weight_total , w.goods_volume_total , w.paid_method , w.pre_pay_amount/100 as pre_pay_amount, w.to_pay_amount/100 as to_pay_amount, w.refund_type , w.cod_amount/100 as cod_amount, w.cod_fee/100 as cod_fee, w.receive_method , w.pickup_fee/100 as pickup_fee, w.delivery_goods_fee/100 as delivery_goods_fee, w.package_fee/100 as package_fee, w.service_fee/100 as service_fee, w.transport_fee/100 as transport_fee, w.insurance_amount/100 as insurance_amount, w.insurance_fee/100 as insurance_fee, w.other_fee/100 as other_fee, w.CREATE_ORG_CODE, w.customer_pickup_org_code , w.active from pkp.t_srv_waybill w
w.delivery_customer_code = #{deliveryCustomerCode,jdbcType=VARCHAR}
AND w.create_time >= #{startTime,jdbcType=TIMESTAMP} AND w.create_time < #{endTime,jdbcType=TIMESTAMP}
and w.active = #{state,jdbcType=VARCHAR}

select count(waybill_no) as countWaybill, sum(PICKUP_FEE /100) as PICKUP_FEE, sum(DELIVERY_GOODS_FEE /100) as DELIVERY_GOODS_FEE, sum(PACKAGE_FEE /100) as PACKAGE_FEE, sum(TRANSPORT_FEE /100) as TRANSPORT_FEE, sum(INSURANCE_FEE /100) as INSURANCE_FEE, sum(COD_FEE /100) as COD_FEE, sum(OTHER_FEE /100) as OTHER_FEE, sum((PICKUP_FEE+DELIVERY_GOODS_FEE+PACKAGE_FEE+TRANSPORT_FEE+INSURANCE_FEE+COD_FEE+OTHER_FEE)/100) as total_amount from pkp.t_srv_waybill
delivery_customer_mobilephone = #{mibilephone,jdbcType=VARCHAR}
AND create_time >= #{startTime,jdbcType=TIMESTAMP} AND create_time < #{endTime,jdbcType=TIMESTAMP}
and active = #{active,jdbcType=VARCHAR}

select w.waybill_no as waybill_no , to_char(w.bill_time, 'yyyy-mm-dd hh24:mi:ss') as shipping_date, w.paid_method as pay_type_code, decode(w.paid_method, 'CD', '银行卡', 'NT', '支票', 'CH', '现金', 'TT', '电汇', 'OL', '网上支付', 'CT', '月结', 'DT', '临时欠款', 'FC', '到付') as pay_type_name, (w.total_fee - w.cod_amount) / 100 as total_amount, decode(w.product_code, 'FLF', '精准卡航', 'AF', '精准空运', 'FSF', '精准城运', 'LRF', '精准汽运(长途)', 'SRF', '精准汽运(短途)', 'AIR_FREIGHT', '空运', 'PLF', '汽运偏线', 'TRANS_VEHICLE', '汽运', 'BGLRF', '精准大票汽运(长)', 'YTY', '场到场', 'BGFLF', '精准大票卡航', 'RCP', '3.60特惠件', 'EPEP', '电商尊享件', 'WVH', '整车（三级）', 'DTD', '门到门', 'PACKAGE', '标准快递', 'BGFSF', '精准大票城运', 'BGSRF', '精准大票汽运(短)', 'PCP', '精准包裹') as trans_type, w.goods_name as cargo_name, f.name as sender_city_name, i.name as consignee_city_name, w.goods_qty_total as cargo_count, w.delivery_customer_contact as poster_name, w.receive_customer_contact as receiver_name from pkp.t_srv_waybill w left join bse.t_bas_org a on w.receive_org_code = a.code and a.active = 'Y' left join bse.t_bas_org b on a.parent_org_code = b.code and b.active = 'Y' left join bse.t_bas_org c on b.parent_org_code = c.code and c.active = 'Y' left join bse.t_bas_org d on c.parent_org_code = d.code and d.active = 'Y' left join bse.t_bas_org e on d.parent_org_code = e.code and e.active = 'Y' left join bse.t_bas_district f on a.city_code = f.code and f.active = 'Y' left join bse.t_bas_org h on w.customer_pickup_org_code = h.code and h.active = 'Y' left join bse.t_bas_district i on h.city_code = i.code and i.active = 'Y'
w.delivery_customer_mobilephone = #{mibilephone,jdbcType=VARCHAR}
AND w.create_time >= #{startTime,jdbcType=TIMESTAMP} AND w.create_time < #{endTime,jdbcType=TIMESTAMP}
and w.active = #{active,jdbcType=VARCHAR}

SELECT P.WAYBILL_NO WAYBILL_NO, P.ORDER_NO ORDER_NO, P.CREATE_ORG_CODE CREATE_ORG_CODE, P.RECEIVE_ORG_CODE RECEIVE_ORG_CODE, P.BILL_TIME BILL_TIME, P.CREATE_TIME CREATE_TIME, P.DELIVERY_CUSTOMER_CODE DELIVERY_CUSTOMER_CODE, P.DELIVERY_CUSTOMER_CONTACT DELIVERY_CUSTOMER_CONTACT, P.RECEIVE_CUSTOMER_CONTACT RECEIVE_CUSTOMER_CONTACT, P.DELIVERY_CUSTOMER_CITY_CODE DELIVERY_CUSTOMER_CITY_CODE, P.CUSTOMER_PICKUP_ORG_CODE CUSTOMER_PICKUP_ORG_CODE, P.CREATE_USER_CODE CREATE_USER_CODE, P.ORDER_CHANNEL ORDER_CHANNEL, P.PRODUCT_CODE PRODUCT_CODE, P.GOODS_WEIGHT_TOTAL GOODS_WEIGHT_TOTAL, P.GOODS_VOLUME_TOTAL GOODS_VOLUME_TOTAL, P.PAID_METHOD PAID_METHOD, P.TOTAL_FEE / 100 TOTAL_FEE, P.COD_AMOUNT / 100 COD_AMOUNT, P.DRIVER_CODE DRIVER_CODE, ORD.GOODS_QTY GOODS_QTY, p.GOODS_QTY_TOTAL GOODS_QTY_TOTAL, ORD.ORDER_STATUS ORDER_STATUS, ORD.VOLUME VOLUME, ORD.WEIGHT WEIGHT, SCSNN.SCAN_TIME SCAN_TIME FROM PKP.T_SRV_WAYBILL P LEFT JOIN PKP.T_SRV_DISPATCH_ORDER ORD ON ORD.ORDER_NO=P.ORDER_NO LEFT JOIN (SELECT DISTINCT S.WAYBILL_NO WAYBILL_NO,SUM(NVL(S.WEIGHT, 0)) GOODS_WEIGHT_TOTAL, COUNT(S.WAYBILL_NO) GOODS_QTY_TOTAL,SUM(NVL(S.VOLUME, 0)) GOODS_VOLUME_TOTAL, MIN(S.SCAN_TIME) SCAN_TIME FROM PKP.T_SRV_PDA_SCAN S WHERE S.ACTIVE = 'Y' AND S.SCAN_TIME >= to_date(to_char(sysdate,'yyyy-mm-dd ') || '00:00:00','yyyy-mm-dd hh24:mi:ss') AND S.SCAN_TIME <= to_date(to_char(sysdate,'yyyy-mm-dd ') || '23:59:59','yyyy-mm-dd hh24:mi:ss')
AND S.WAYBILL_TYPE = #{waybillType,jdbcType=VARCHAR}
AND S.SCAN_TYPE = #{scanType,jdbcType=VARCHAR}
AND S.WHETHER_COMPLETE = #{whetherComplete,jdbcType=VARCHAR}
AND S.CREATE_ORG_CODE = #{currentDeptCode,jdbcType=VARCHAR}
AND S.WAYBILL_NO IN
#{eWaybillNo,jdbcType=VARCHAR}
AND S.ORDER_NO IN
#{orderNo,jdbcType=VARCHAR}
AND S.DRIVER_CODE IN
#{driverCode,jdbcType=VARCHAR}
GROUP BY S.WAYBILL_NO) SCSNN ON SCSNN.WAYBILL_NO = P.WAYBILL_NO WHERE 1=1
AND (P.is_ecs != 'Y' or P.is_ecs is null )
AND (P.RECEIVE_ORG_CODE=#{currentDeptCode,jdbcType=VARCHAR} OR P.CREATE_ORG_CODE=#{currentDeptCode,jdbcType=VARCHAR})
AND P.ACTIVE=#{active,jdbcType=VARCHAR}
AND ORD.WAYBILL_TYPE=#{waybillType,jdbcType=VARCHAR}
AND P.WAYBILL_NO IN
#{eWaybillNo,jdbcType=VARCHAR}
AND P.ORDER_NO IN
#{orderNo,jdbcType=VARCHAR}
AND P.DELIVERY_CUSTOMER_CODE IN
#{customerCode,jdbcType=VARCHAR}
AND ORD.ORDER_TIME >= #{billStartTime,jdbcType=TIMESTAMP}
AND ORD.ORDER_TIME <= #{billEndTime,jdbcType=TIMESTAMP}

		select distinct d.rate
		from pkp.T_SRV_WAYBILL_DIS_DETAIL d
		where
    			d.WAYBILL_NO = #{waybillNo}
    		 and d.pricing_entry_code = #{entryCode} 
    		 and d.TYPE = #{type}

select SIGN_SITUATION AS SIGN_STATUS,WAYBILL_NO from pkp.T_SRV_WAYBILL_SIGN_RESULT where WAYBILL_NO in
#{waybillNo,jdbcType=VARCHAR}
and active = 'Y'

	  select	/*模块：接送货-运单查询-根据运单号和订单号查询运单基本信息*/

		from PKP.T_SRV_WAYBILL
		where 1 = 1

				and ACTIVE = #{active,jdbcType=CHAR}

				and WAYBILL_NO IN


    	      	#{waybillNo,jdbcType=VARCHAR} 



				and ORDER_NO = #{orderNo,jdbcType=VARCHAR}

		/*order by CREATE_TIME desc*/

select /*模块：接送货-运单-查询运单*/

from PKP.T_SRV_WAYBILL p


	P.PENDING_TYPE NOT IN ( 'RFC_ACTIVE','RFC_PENDING','PDA_PENDING')
	AND
	p.active = #{waybillEntity.active,jdbcType=VARCHAR}

		and p.BILL_TIME    >=  
		#{billStartTime,jdbcType=TIMESTAMP}


		AND p.BILL_TIME    <=  
		#{billEndTime,jdbcType=TIMESTAMP}


		and p.CREATE_TIME   >=  
		#{createStartTime,jdbcType=TIMESTAMP}


		and p.CREATE_TIME   <=  
		#{createEndTime,jdbcType=TIMESTAMP}


		and p.receive_org_code =
		#{waybillEntity.receiveOrgCode,jdbcType=VARCHAR}


		and p.receive_org_code in
		 #{receiveOrgCode,
			jdbcType=VARCHAR}



		and p.product_code =
		#{waybillEntity.productCode,jdbcType=VARCHAR}

	 and p.product_code not in ('PACKAGE','RCP','EPEP')

		and p.pending_type =
		#{waybillEntity.pendingType,jdbcType=VARCHAR}


		and p.create_user_code =
		#{waybillEntity.createUserCode,jdbcType=VARCHAR}


		and p.WAYBILL_NO = #{mixNo,jdbcType=CHAR}


		and p.ORDER_NO =#{orderNo,jdbcType=VARCHAR} and
		p.ORDER_NO is not null


order by BILL_TIME asc
