			 AND DETAIL.LONG_OR_SHORT  = #{longOrShort}


			 AND DETAIL.T_SRV_PRODUCT_CODE  = #{productCode}


			 AND DETAIL.ARRV_REGION_ID  = #{arrvRegionId}


			 AND DETAIL.ARRV_REGION_CODE  = #{arrvRegionCode}


			 AND DETAIL.CREATE_USER_CODE  = #{createUser}


