mapper.select:
select /*综合-价格-查询价格方案下载信息*/

from PKP.T_SRV_PRICE_PLAN


	 	T_SRV_PRICE_REGION_ID = #{deptRegionId,jdbcType=VARCHAR}	


	 	AND ACTIVE = #{active,jdbcType=VARCHAR}	


	 	AND BEGIN_TIME  <=  #{billDate,jdbcType=TIMESTAMP}
	 	AND END_TIME > #{billDate,jdbcType=TIMESTAMP}

select /*综合-价格-根据始发区域ID查询价格方案*/

from PKP.T_SRV_PRICE_PLAN
where T_SRV_PRICE_REGION_ID = #{deptRegionId,jdbcType=VARCHAR}	
AND ACTIVE = #{active,jdbcType=VARCHAR}	
AND BEGIN_TIME  <=  #{billDate,jdbcType=TIMESTAMP}
AND END_TIME > #{billDate,jdbcType=TIMESTAMP}

select /*综合-价格-根据主键ID查询价格方案*/ 

from PKP.T_SRV_PRICE_PLAN
where ID = #{id,jdbcType=VARCHAR}

mapper.delete:
delete /*综合-价格-删除价格方案*/ 
from PKP.T_SRV_PRICE_PLAN
where ID = #{id,jdbcType=VARCHAR}

mapper.select:
    select /*综合-价格-价格方案主信息查询*/ 

    from PKP.T_SRV_PRICE_PLAN


			AND T_SRV_PRICE_REGION_ID = #{priceRegionId,jdbcType=VARCHAR}


			AND T_SRV_PRICE_REGION_CODE = #{priceRegionCode,jdbcType=VARCHAR}


			AND ACTIVE = #{active,jdbcType=VARCHAR}


			AND NAME  like   CONCAT(CONCAT('%', #{name,jdbcType=VARCHAR}), '%')      


			AND CUSTOMER_CODE = #{customerCode,jdbcType=VARCHAR}


			AND VERSION_NO  >=  #{versionNo}        


			AND TRANSPORT_FLAG = #{transportFlag,jdbcType=VARCHAR}



	  			AND  BEGIN_TIME   <=   sysdate
				AND  END_TIME    >    sysdate


	  			AND  END_TIME    <    sysdate



					AND BEGIN_TIME  >=  #{beginTime,jdbcType=TIMESTAMP} 




				   AND BEGIN_TIME   <=  #{endTime,jdbcType=TIMESTAMP}




			AND BEGIN_TIME   >=  #{beginTime,jdbcType=TIMESTAMP} 




			AND BEGIN_TIME   <=  #{endTime,jdbcType=TIMESTAMP} 


		order by VERSION_NO,ID ASC

    select /*综合-价格-价格方案主信息查询*/ 

    from PKP.T_SRV_PRICE_PLAN


			AND T_SRV_PRICE_REGION_ID = #{priceRegionId,jdbcType=VARCHAR}


			AND T_SRV_PRICE_REGION_CODE = #{priceRegionCode,jdbcType=VARCHAR}


			AND ACTIVE = #{active,jdbcType=VARCHAR}


			AND NAME  = #{name,jdbcType=VARCHAR}


			AND CUSTOMER_CODE = #{customerCode,jdbcType=VARCHAR}


			AND VERSION_NO  >=  #{versionNo}        


			AND TRANSPORT_FLAG = #{transportFlag,jdbcType=VARCHAR}



	  			AND  BEGIN_TIME   <=   sysdate
				AND  END_TIME    >    sysdate


	  			AND  END_TIME    <    sysdate



					AND BEGIN_TIME  >=  #{beginTime,jdbcType=TIMESTAMP} 


					AND END_TIME   <=  #{endTime,jdbcType=TIMESTAMP} 




			AND BEGIN_TIME   >=  #{beginTime,jdbcType=TIMESTAMP} 


			AND END_TIME   <=  #{endTime,jdbcType=TIMESTAMP} 


		order by VERSION_NO,ID ASC

    select  /*综合-价格-价格方案主信息总记录查询*/ 
    COUNT(*) from PKP.T_SRV_PRICE_PLAN


			AND T_SRV_PRICE_REGION_ID = #{priceRegionId,jdbcType=VARCHAR}


			AND T_SRV_PRICE_REGION_CODE = #{priceRegionCode,jdbcType=VARCHAR}


			AND ACTIVE = #{active,jdbcType=VARCHAR}


			AND NAME  like   CONCAT(CONCAT('%', #{name,jdbcType=VARCHAR}), '%')      


			AND CUSTOMER_CODE = #{customerCode,jdbcType=VARCHAR}


			AND BEGIN_TIME   >=  #{beginTime,jdbcType=TIMESTAMP} 




			AND BEGIN_TIME   <=  #{endTime,jdbcType=TIMESTAMP}


			AND TRANSPORT_FLAG = #{transportFlag,jdbcType=VARCHAR}



	  			AND  BEGIN_TIME   <=   sysdate
				AND  END_TIME    >    sysdate


	  			AND  END_TIME    <    sysdate



					AND BEGIN_TIME  >=  #{beginTime,jdbcType=TIMESTAMP} 




					AND BEGIN_TIME   <=  #{endTime,jdbcType=TIMESTAMP}

 SELECT /*综合-价格-查询价格明细信息*/ 

	FROM 
	PKP.T_SRV_PRICE_PLAN pla 
	INNER JOIN PKP.T_SRV_PRICING_VALUATION va ON pla.id = va.PRICE_PLAN_ID
	INNER JOIN PKP.T_SRV_PRICING_CRITERIA_DETAIL detail ON va.ID = detail.PRICING_VALUATION_ID
	INNER JOIN PKP.T_SRV_PRODUCT pro ON pro.code = va.product_code
	INNER JOIN PKP.T_SRV_GOODSTYPE goods ON goods.code = va.goods_type_code
	AND pla.id =  #{pricePlanId ,jdbcType=VARCHAR} 

		AND va.ID = #{valuationId ,jdbcType=VARCHAR}         


		AND va.TYPE = #{valuationType ,jdbcType=VARCHAR}         


		AND va.ARRV_REGION_ID  = #{arrvRegionId ,jdbcType=VARCHAR}	



		AND va.DEPT_REGION_ID  = #{deptRegionId ,jdbcType=VARCHAR}		



		AND va.PRODUCT_CODE  = #{productCode ,jdbcType=VARCHAR}	


		AND va.GOODS_TYPE_CODE  = #{goodsTypeCode ,jdbcType=VARCHAR}	


		AND va.FLIGHT_SHIFT  = #{flightTypeCode ,jdbcType=VARCHAR}		

	order by detail.pricing_valuation_id

 SELECT /*综合-价格-查询价格明细信息*/ 
	COUNT(*)
	FROM 
	PKP.T_SRV_PRICE_PLAN pla 
	INNER JOIN PKP.T_SRV_PRICING_VALUATION va ON pla.id = va.PRICE_PLAN_ID
	INNER JOIN PKP.T_SRV_PRICING_CRITERIA_DETAIL detail ON va.ID = detail.PRICING_VALUATION_ID
	INNER JOIN PKP.T_SRV_PRODUCT pro ON pro.code = va.product_code
	INNER JOIN PKP.T_SRV_GOODSTYPE goods ON goods.code = va.goods_type_code
	AND pla.id =  #{pricePlanId ,jdbcType=VARCHAR}  

		AND va.ID = #{valuationId ,jdbcType=VARCHAR}         


		AND va.TYPE = #{valuationType ,jdbcType=VARCHAR}         


		AND va.ARRV_REGION_ID  = #{arrvRegionId ,jdbcType=VARCHAR}	



		AND va.DEPT_REGION_ID  = #{deptRegionId ,jdbcType=VARCHAR}		



		AND va.PRODUCT_CODE  = #{productCode ,jdbcType=VARCHAR}	


		AND va.GOODS_TYPE_CODE  = #{goodsTypeCode ,jdbcType=VARCHAR}	


		AND va.FLIGHT_SHIFT  = #{flightTypeCode ,jdbcType=VARCHAR}		



	order by detail.pricing_valuation_id

 SELECT /*综合-价格-查询同一始发区域下的方案价格明细信息*/ 

	FROM 
	PKP.T_SRV_PRICE_PLAN pla 
	INNER JOIN PKP.T_SRV_PRICING_VALUATION va ON pla.id = va.PRICE_PLAN_ID
	INNER JOIN PKP.T_SRV_PRICING_CRITERIA_DETAIL detail ON va.ID = detail.PRICING_VALUATION_ID
	INNER JOIN PKP.T_SRV_PRODUCT pro ON pro.id = va.product_id  
	INNER JOIN PKP.T_SRV_GOODSTYPE goods ON goods.id = va.goods_type_id
	AND pla.T_SRV_PRICE_REGION_ID =  #{priceRegionId ,jdbcType=VARCHAR} 
	AND pla.active  = #{active ,jdbcType=VARCHAR}						  
	AND va.ARRV_REGION_ID  = #{arrvRegionId ,jdbcType=VARCHAR}			
	AND va.CENTRALIZE_PICKUP = #{centralizePickup ,jdbcType=VARCHAR}	
	AND va.PRODUCT_CODE  = #{productCode ,jdbcType=VARCHAR}				
	AND va.GOODS_TYPE_CODE  = #{goodsTypeCode ,jdbcType=VARCHAR}		
	AND #{beginTime ,jdbcType=TIMESTAMP} BETWEEN pla.BEGIN_TIME AND pla.END_TIME

     SELECT /*综合-价格-查询同一始发区域下的空运价格明细信息*/ 

    	FROM 
    	PKP.T_SRV_PRICE_PLAN pla 
    	INNER JOIN PKP.T_SRV_PRICING_VALUATION va ON pla.id = va.PRICE_PLAN_ID
    	INNER JOIN PKP.T_SRV_PRICING_CRITERIA_DETAIL detail ON va.ID = detail.PRICING_VALUATION_ID
    	INNER JOIN PKP.T_SRV_PRODUCT pro ON pro.id = va.product_id  
    	INNER JOIN PKP.T_SRV_GOODSTYPE goods ON goods.id = va.goods_type_id
    	AND pla.T_SRV_PRICE_REGION_ID =  #{priceRegionId ,jdbcType=VARCHAR} 
    	AND pla.active  = #{active ,jdbcType=VARCHAR}						  
    	AND va.ARRV_REGION_ID  = #{arrvRegionId ,jdbcType=VARCHAR}			
    	AND va.CENTRALIZE_PICKUP = #{centralizePickup ,jdbcType=VARCHAR}	
    	AND va.PRODUCT_CODE  = #{productCode ,jdbcType=VARCHAR}				
    	AND va.GOODS_TYPE_CODE  = #{goodsTypeCode ,jdbcType=VARCHAR}		
    	AND (
    		 ( #{beginTime ,jdbcType=TIMESTAMP}  >=  pla.BEGIN_TIME 
		   AND #{beginTime ,jdbcType=TIMESTAMP}  <=  pla.END_TIME ) 
    	OR	 ( #{endTime ,jdbcType=TIMESTAMP}  >=  pla.BEGIN_TIME 
		   AND #{endTime ,jdbcType=TIMESTAMP}  <=  pla.END_TIME ) 
    	OR   (
    			#{beginTime,jdbcType=TIMESTAMP}  <  pla.begin_time
    		AND #{endTime,jdbcType=TIMESTAMP}  >  pla.end_time
    		 )
    	   )

 SELECT /*综合-价格-增加方案明细判定是否有重复记录*/ 

	FROM 
	PKP.T_SRV_PRICE_PLAN pla 
	INNER JOIN PKP.T_SRV_PRICING_VALUATION va ON pla.id = va.PRICE_PLAN_ID
	INNER JOIN PKP.T_SRV_PRICING_CRITERIA_DETAIL detail ON va.ID = detail.PRICING_VALUATION_ID
	INNER JOIN PKP.T_SRV_PRODUCT pro ON pro.code = va.product_code  
	INNER JOIN PKP.T_SRV_GOODSTYPE goods ON goods.code = va.goods_type_code
	AND pla.ID =  #{pricePlanId ,jdbcType=VARCHAR} 			
	AND va.ARRV_REGION_ID  = #{arrvRegionId ,jdbcType=VARCHAR}			
	AND va.CENTRALIZE_PICKUP = #{centralizePickup ,jdbcType=VARCHAR}	
	AND va.PRODUCT_CODE  = #{productCode ,jdbcType=VARCHAR}				
	AND va.GOODS_TYPE_CODE  = #{goodsTypeCode ,jdbcType=VARCHAR}		

		AND va.FLIGHT_SHIFT  = #{flightTypeCode ,jdbcType=VARCHAR}		


		AND va.ID  != #{valuationId ,jdbcType=VARCHAR}		


		AND detail.COMB_BILL_TYPE_CODE  = #{combBillTypeCode ,jdbcType=VARCHAR}		



		AND va.DEPT_REGION_ID  = #{priceRegionId ,jdbcType=VARCHAR}

mapper.update:

  		UPDATE /*综合-价格-增加方案明细判定是否有重复记录*/ 
  		pkp.T_SRV_PRICE_PLAN T SET T.ACTIVE = 'Y', T.VERSION_NO = #{versionNo,jdbcType=DECIMAL}

		WHERE T.ID IN 

			#{pricePlanId}

mapper.select:
select /*整车价格参数方案信息查询*/ p.id, p.scenario_name, o.organization_code, o.organization_id, o.organization_name, p.active, p.begin_time, p.MODIFY_TIME, p.MODIFY_USER, p.end_time, p.version, p.remark from pkp.t_srv_price_car_plan p left join pkp.t_srv_price_car_plan_org o on o.price_car_plan_id = p.id
AND p.scenario_name like CONCAT(CONCAT('%', #{scenarioName,jdbcType=VARCHAR}), '%')
AND o.organization_code = #{organizationCode,jdbcType=VARCHAR}
AND p.ACTIVE = #{active,jdbcType=VARCHAR}
AND (p.BEGIN_TIME <= sysdate and p.END_TIME >= sysdate )
AND (p.BEGIN_TIME > sysdate or p.END_TIME < sysdate )
AND p.id = #{id,jdbcType=VARCHAR}
order by version ASC

select /*综合-价格-价格方案主信息总记录查询*/ count(*) from PKP.T_SRV_PRICE_CAR_PLAN p left join pkp.t_srv_price_car_plan_org o on o.price_car_plan_id = p.id
AND p.scenario_name like CONCAT(CONCAT('%', #{scenarioName,jdbcType=VARCHAR}), '%')
AND o.organization_code = #{organizationCode,jdbcType=VARCHAR}
AND p.ACTIVE = #{active,jdbcType=VARCHAR}
AND (p.BEGIN_TIME <= sysdate and p.END_TIME >= sysdate )
AND (p.BEGIN_TIME > sysdate or p.END_TIME < sysdate )

select /*整车价格参数方案信息查询*/ id , scenario_name , active , version , create_time , create_user , begin_time , end_time, modify_time , modify_user , remark from PKP.T_SRV_PRICE_CAR_PLAN
AND scenario_name = #{scenarioName,jdbcType=VARCHAR}
AND ACTIVE = #{active,jdbcType=VARCHAR}
AND version = #{version,jdbcType=VARCHAR}

mapper.delete:
delete /*删除整车价格参数方案明细*/ 
from  PKP.T_SRV_PRICE_CAR_PLAN_DETAIL
where ID = #{id,jdbcType=VARCHAR}

delete /*通过价格方案id删除整车价格参数方案明细*/ 
from  PKP.T_SRV_PRICE_CAR_PLAN_DETAIL
where PRICE_CAR_PLAN_ID = #{id,jdbcType=VARCHAR}

delete /*删除整车价格参数方案所适应部门*/ 
from  pkp.t_srv_price_car_plan_org
where PRICE_CAR_PLAN_ID = #{id,jdbcType=VARCHAR}

delete /*删除整车价格参数方案*/ 
from  PKP.T_SRV_PRICE_CAR_PLAN
where ID = #{id,jdbcType=VARCHAR}

mapper.select:
select /*导出整车价格参数方案明细*/ p.scenario_name, o.organization_name, d.invoice_type, d.float_up, d.float_down, d.remark, d.other_cost_parameter, d.package_fee_parameter, d.weight_parameter, d.car_cost_parameter, d.humen_cost_parameter from pkp.t_srv_price_car_plan p left join pkp.t_srv_price_car_plan_org o on o.price_car_plan_id = p.id left join pkp.t_srv_price_car_plan_detail d on d.price_car_plan_id = p.id
AND p.scenario_name like CONCAT(CONCAT('%', #{scenarioName,jdbcType=VARCHAR}), '%')
AND o.organization_code = #{organizationCode,jdbcType=VARCHAR}
AND p.ACTIVE = #{active,jdbcType=VARCHAR}
AND (p.BEGIN_TIME <= sysdate and p.END_TIME >= sysdate )
AND (p.BEGIN_TIME > sysdate or p.END_TIME < sysdate )
AND d.invoice_type = #{invoiceType,jdbcType=VARCHAR}
AND p.id = #{id,jdbcType=VARCHAR}

select /*通过组织编码查询整车价格参数方案明细*/ p.scenario_name, o.organization_name, d.invoice_type, d.float_up, d.float_down, d.remark, d.other_cost_parameter, d.package_fee_parameter, d.weight_parameter, d.car_cost_parameter, d.humen_cost_parameter from pkp.t_srv_price_car_plan p left join pkp.t_srv_price_car_plan_org o on o.price_car_plan_id = p.id left join pkp.t_srv_price_car_plan_detail d on d.price_car_plan_id = p.id
AND o.organization_code = #{organizationCode,jdbcType=VARCHAR}
AND p.ACTIVE = #{active,jdbcType=VARCHAR}
AND (p.BEGIN_TIME <= sysdate and p.END_TIME >= sysdate )
AND d.invoice_type = #{invoiceType,jdbcType=VARCHAR}

    select  /*综合-价格-价格方案主信息总记录查询*/ 
    COUNT(*) from PKP.T_SRV_PRICE_PLAN


			AND CUSTOMER_CODE = #{customerCode,jdbcType=VARCHAR}


			AND ACTIVE = #{active,jdbcType=VARCHAR}

			AND END_TIME   >=  sysdate

 SELECT /*综合-价格-查询同一始发区域下的方案价格明细信息*/ 

	FROM 
	PKP.T_SRV_PRICE_PLAN pla 
	INNER JOIN PKP.T_SRV_PRICING_VALUATION va ON pla.id = va.PRICE_PLAN_ID
	INNER JOIN PKP.T_SRV_PRODUCT pro ON pro.id = va.product_id  
	INNER JOIN PKP.T_SRV_GOODSTYPE goods ON goods.id = va.goods_type_id
	AND va.DEPT_REGION_ID =  #{priceRegionId ,jdbcType=VARCHAR} 
	AND pla.ID =  #{pricePlanId ,jdbcType=VARCHAR} 
	AND va.ARRV_REGION_ID  = #{arrvRegionId ,jdbcType=VARCHAR}			
	AND va.CENTRALIZE_PICKUP = #{centralizePickup ,jdbcType=VARCHAR}	
	AND va.PRODUCT_CODE  = #{productCode ,jdbcType=VARCHAR}				
	AND va.GOODS_TYPE_CODE  = #{goodsTypeCode ,jdbcType=VARCHAR}

    select /*综合-价格-价格方案主信息查询*/ 

    from PKP.T_SRV_PRICE_PLAN


			AND ACTIVE = #{active,jdbcType=VARCHAR}


			AND CUSTOMER_CODE = #{customerCode,jdbcType=VARCHAR}


					AND BEGIN_TIME  <=  #{currentDate,jdbcType=TIMESTAMP} 



				   AND END_TIME   >=  #{currentDate,jdbcType=TIMESTAMP}
