mapper.select:

SELECT /** 综合-价格-根据综合条件查询价格区域 **/ DISTINCT



FROM pkp.T_SRV_EXPRESS_PRICE_REGION MASTER
LEFT JOIN pkp.T_SRV_EXPRESS_PRICE_REGION_ORG MASTERORG
ON MASTER.ID = MASTERORG.T_SRV_EXPRESS_PR_REGION_ID



 MASTER.BEGIN_TIME <= SYSDATE 
			  AND MASTER.END_TIME >= SYSDATE 

	AND MASTER.ID = #{id}


	AND MASTER.CODE  = #{regionCode}


	AND MASTER.NAME like #{regionName}


	AND MASTER.TYPE = #{regionType}


	AND MASTER.ACTIVE  = #{active}


	AND MASTER.NATION_CODE = #{nationCode}


	AND MASTER.PROV_CODE = #{proCode}


	AND  MASTER.CITY_CODE = #{cityCode}


	AND MASTER.COUNTY_CODE like #{countyCode}


	AND MASTERORG.INCLUDE_ORG_CODE = #{deptCode}


	 AND MASTERORG.BEGIN_TIME <= #{beginTime} AND MASTERORG.END_TIME > #{beginTime} 



	 AND MASTERORG.BEGIN_TIME <= #{billDate} 
			  AND MASTERORG.END_TIME > #{billDate} 





ORDER BY MASTER.MODIFY_TIME DESC


SELECT /** 综合-价格-根据行政区域联接字符串CODE查询价格区域 **/ DISTINCT



FROM pkp.T_SRV_EXPRESS_PRICE_REGION MASTER


MASTER.NATION_CODE||MASTER.PROV_CODE||MASTER.CITY_CODE||MASTER.COUNTY_CODE = #{administrativeRegionCodes}

SELECT /** 综合-价格-根据条件查询价格区域 **/

FROM pkp.T_SRV_EXPRESS_PRICE_REGION MASTER 


	MASTER.ACTIVE = #{active}


	AND MASTER.VERSION_NO > #{versionNo}        


	order by MASTER.VERSION_NO ASC

SELECT  /** 综合-价格-根据条件查询价格区域部门信息 **/

FROM pkp.T_SRV_EXPRESS_PRICE_REGION_ORG


	ACTIVE = #{active}


	AND VERSION_NO > #{versionNo}        


	order by VERSION_NO ASC


SELECT /** 综合-价格-根据条件查询价格区域总数  **/ COUNT(1)


FROM pkp.T_SRV_EXPRESS_PRICE_REGION MASTER



 MASTER.BEGIN_TIME <= SYSDATE 
			  AND MASTER.END_TIME >= SYSDATE 

	MASTER.ID = #{id}


	AND MASTER.CODE  = #{regionCode}


	AND MASTER.NAME like #{regionName}


	AND MASTER.TYPE = #{regionType}


	AND MASTER.ACTIVE  = #{active}


	AND MASTER.NATION_CODE = #{nationCode}


	AND MASTER.PROV_CODE = #{proCode}


	AND  MASTER.CITY_CODE = #{cityCode}


	AND MASTER.COUNTY_CODE like #{countyCode}

mapper.update:
UPDATE /** 综合-价格-根据ID集合批量激活价格区域 **/ pkp.T_SRV_EXPRESS_PRICE_REGION T SET T.ACTIVE = 'Y'
T.ID IN
#{regionId}

DELETE /** 综合-价格-根据ID集合批量删除价格区域 **/ FROM pkp.T_SRV_EXPRESS_PRICE_REGION T
T.ID IN
#{regionId}

DELETE /** 综合-价格-根据ID集合批量删除价格区域和部门 **/ FROM pkp.T_SRV_EXPRESS_PRICE_REGION_ORG T
T.T_SRV_EXPRESS_PR_REGION_ID IN
#{regionId}

mapper.insert:
INSERT /** 综合-价格-插入价格区域 **/ INTO pkp.T_SRV_EXPRESS_PRICE_REGION(
) values ( #{id, jdbcType=VARCHAR}, #{regionCode, jdbcType=VARCHAR}, #{regionName, jdbcType=VARCHAR}, #{regionType, jdbcType=VARCHAR}, #{active}, #{beginTime,jdbcType=TIMESTAMP}, #{endTime, jdbcType=TIMESTAMP}, #{description, jdbcType=VARCHAR}, #{versionNo, jdbcType=NUMERIC}, #{nationCode, jdbcType=VARCHAR}, #{proCode, jdbcType=VARCHAR}, #{cityCode, jdbcType=VARCHAR}, #{countyCode, jdbcType=VARCHAR}, #{createUser, jdbcType=VARCHAR}, #{modifyUser, jdbcType=VARCHAR}, sysdate, sysdate, #{createOrgCode, jdbcType=VARCHAR}, #{modifyOrgCode, jdbcType=VARCHAR}
)

mapper.select:

SELECT /** 综合-价格-根据条件查询价格区域关联部门  **/ 



FROM pkp.T_SRV_EXPRESS_PRICE_REGION_ORG MASTER



	MASTER.T_SRV_EXPRESS_PR_REGION_ID = #{priceRegionId}


 AND MASTER.ACTIVE = #{active,jdbcType=VARCHAR}



 AND MASTER.INCLUDE_ORG_CODE = #{includeOrgCode,jdbcType=VARCHAR}



AND  MASTER.BEGIN_TIME   <=   #{billDate,jdbcType=TIMESTAMP} 
AND  MASTER.END_TIME     >    #{billDate,jdbcType=TIMESTAMP} 



ORDER BY MASTER.MODIFY_TIME DESC


SELECT /** 综合-价格-根据行政区域查询逻辑区域信息  **/ 



FROM pkp.T_SRV_EXPRESS_PRICE_REGION MASTER




	MASTER.ACTIVE = #{active}


	AND MASTER.TYPE = #{regionType}


	AND (MASTER.COUNTY_CODE = #{countyCode} or MASTER.CITY_CODE = #{cityCode}
	or MASTER.PROV_CODE = #{proCode})


	AND ( MASTER.CITY_CODE = #{cityCode} or MASTER.PROV_CODE = #{proCode})


	AND MASTER.PROV_CODE = #{proCode}


	AND MASTER.NATION_CODE = #{nationCode}


	 AND MASTER.BEGIN_TIME <= #{billDate,jdbcType=TIMESTAMP} AND MASTER.END_TIME > #{billDate,jdbcType=TIMESTAMP} 



ORDER BY MASTER.MODIFY_TIME DESC


SELECT /** 综合-价格-根据行政区域查询逻辑区域信息  **/ 



FROM pkp.T_SRV_EXPRESS_PRICE_REGION MASTER			 



	MASTER.ACTIVE = #{active}


	AND MASTER.TYPE = #{regionType}


	AND (MASTER.COUNTY_CODE = #{countyCode} or MASTER.CITY_CODE = #{cityCode})


	AND ( MASTER.CITY_CODE = #{cityCode})


	AND MASTER.NATION_CODE = #{nationCode}


	 AND MASTER.BEGIN_TIME <= #{billDate,jdbcType=TIMESTAMP} AND MASTER.END_TIME > #{billDate,jdbcType=TIMESTAMP} 



ORDER BY MASTER.MODIFY_TIME DESC


	SELECT /** 综合-价格-根据区域名称批量查找区域信息  **/ DISTINCT



	FROM pkp.T_SRV_EXPRESS_PRICE_REGION MASTER




		  MASTER.ACTIVE  = #{active}


      AND  MASTER.NAME IN 

		#{regionName}  



		 AND MASTER.BEGIN_TIME <= #{billDate} AND MASTER.END_TIME > #{billDate}


	SELECT /** 综合-价格-根据区域CODE批量查找区域信息  **/ 
	DISTINCT



	FROM pkp.T_SRV_EXPRESS_PRICE_REGION MASTER



		  MASTER.ACTIVE  = #{active}


      AND  MASTER.CODE IN 

		#{regionCode}  



		 AND MASTER.BEGIN_TIME <= #{billDate} AND MASTER.END_TIME > #{billDate}


SELECT /** 综合-价格-根据区域CODE查找区域信息  **/



FROM pkp.T_SRV_EXPRESS_PRICE_REGION MASTER



	MASTER.ACTIVE = #{active}


	AND (MASTER.COUNTY_CODE = #{countyCode} or MASTER.CITY_CODE = #{cityCode})


	AND ( MASTER.CITY_CODE = #{cityCode})


	 AND MASTER.BEGIN_TIME <= #{billDate,jdbcType=TIMESTAMP} AND MASTER.END_TIME > #{billDate,jdbcType=TIMESTAMP}

SELECT /** 综合-价格-网点CODE查询价格区域关联部门 **/
FROM pkp.T_SRV_EXPRESS_PRICE_REGION_ORG MASTER
AND MASTER.ACTIVE = 'Y' AND MASTER.INCLUDE_ORG_CODE = #{deptNo,jdbcType=VARCHAR}


SELECT /** 综合-价格-根据综合条件查询价格区域 **/ DISTINCT



FROM pkp.T_SRV_EXPRESS_PRICE_REGION T
LEFT JOIN pkp.T_SRV_EXPRESS_PRICE_REGION_ORG MASTER
ON T.ID = MASTER.T_SRV_EXPRESS_PR_REGION_ID



	T.ID = #{id}


	 AND MASTER.BEGIN_TIME <= #{billDate} AND MASTER.END_TIME > #{billDate}


SELECT /** 综合-价格-根据名称查询价格区域 **/



FROM pkp.T_SRV_EXPRESS_PRICE_REGION MASTER



	 MASTER.NAME = #{regionName}


	 AND MASTER.END_TIME > #{beginTime} 



ORDER BY MASTER.MODIFY_TIME DESC


SELECT /** 综合-价格-根据综合条件查询价格区域 **/ DISTINCT



FROM pkp.T_SRV_EXPRESS_PRICE_REGION MASTER



 MASTER.BEGIN_TIME <= SYSDATE 
			  AND MASTER.END_TIME > SYSDATE 

	AND MASTER.ID = #{id}


	AND MASTER.CODE  = #{regionCode}


	AND MASTER.NAME like #{regionName}


	AND MASTER.TYPE = #{regionType}


	AND MASTER.ACTIVE  = #{active}


	AND MASTER.NATION_CODE = #{nationCode}


	AND MASTER.PROV_CODE = #{proCode}


	AND  MASTER.CITY_CODE = #{cityCode}


	AND MASTER.COUNTY_CODE = #{countyCode}



ORDER BY MASTER.MODIFY_TIME DESC


SELECT /** 综合-价格-根据综合条件查询价格区域 **/ DISTINCT



FROM pkp.T_SRV_EXPRESS_PRICE_REGION MASTER



	AND MASTER.ID = #{id}


	AND MASTER.CODE  = #{regionCode}


	AND MASTER.NAME like #{regionName}


	AND MASTER.TYPE = #{regionType}


	AND MASTER.ACTIVE  = #{active}


	AND ((MASTER.NATION_CODE = #{nationCode} and MASTER.PROV_CODE is null)
	or (MASTER.PROV_CODE = #{proCode} and MASTER.CITY_CODE is null))



	AND 
	((MASTER.CITY_CODE = #{cityCode} and MASTER.COUNTY_CODE is null)
	or (MASTER.PROV_CODE = #{proCode} and MASTER.CITY_CODE is null)
	or (MASTER.NATION_CODE = #{nationCode} and MASTER.PROV_CODE is null))




	AND 
	(MASTER.COUNTY_CODE = #{countyCode}
	or (MASTER.CITY_CODE = #{cityCode} and MASTER.COUNTY_CODE is null)
	or (MASTER.PROV_CODE = #{proCode} and MASTER.CITY_CODE is null)
	or (MASTER.NATION_CODE = #{nationCode} and MASTER.PROV_CODE is null))



	 AND MASTER.BEGIN_TIME <= #{billDate} AND MASTER.END_TIME > #{billDate} 



ORDER BY MASTER.MODIFY_TIME DESC
