mapper.select:

SELECT /** 综合-价格-根据综合条件查询增值价格区域 **/ DISTINCT



FROM PKP.T_SRV_VALUEADD_REGION MASTER
LEFT JOIN PKP.T_SRV_VALUEADD_REGION_ORG MASTERORG
ON MASTER.ID = MASTERORG.T_SRV_PRICE_REGION_ID AND MASTERORG.ACTIVE = 'Y' 



	MASTER.ID = #{id}


	AND MASTER.CODE  = #{regionCode}


	AND MASTER.NAME like #{regionName}


 AND MASTER.BEGIN_TIME <= #{queryDate} AND MASTER.END_TIME > #{queryDate} 


	AND MASTER.TYPE = #{regionType}


	AND MASTER.ACTIVE  = #{active}


	AND MASTER.NATION_CODE = #{nationCode}


	AND MASTER.PROV_CODE = #{proCode}


	AND  MASTER.CITY_CODE = #{cityCode}


	AND MASTER.COUNTY_CODE like #{countyCode}


	AND MASTERORG.INCLUDE_ORG_CODE = #{deptCode}


	 AND MASTER.BEGIN_TIME <= #{beginTime} AND MASTER.END_TIME > #{beginTime} 



	 AND MASTERORG.BEGIN_TIME <= #{billDate} 
			  AND MASTERORG.END_TIME > #{billDate} 



ORDER BY MASTER.MODIFY_TIME DESC


SELECT /** 综合-价格-根据行政区域联接字符串CODE查询增值价格区域 **/ DISTINCT



FROM pkp.t_srv_valueadd_region MASTER


MASTER.NATION_CODE||MASTER.PROV_CODE||MASTER.CITY_CODE||MASTER.COUNTY_CODE = #{administrativeRegionCodes}
AND MASTER.END_TIME > #{now}

SELECT /** 综合-价格-根据条件查询增值价格区域 **/

FROM pkp.t_srv_valueadd_region MASTER 


	MASTER.ACTIVE = #{active}


	AND MASTER.VERSION_NO >= #{versionNo}        


	order by MASTER.VERSION_NO,MASTER.ID ASC

SELECT  /** 综合-价格-根据条件查询增值价格区域部门信息 **/

FROM pkp.t_srv_valueadd_region_org


	ACTIVE = #{active}


	AND VERSION_NO >= #{versionNo}        


	order by VERSION_NO,ID ASC


SELECT /** 综合-价格-根据条件查询增值价格区域总数  **/ COUNT(DISTINCT MASTER.ID)


FROM PKP.T_SRV_VALUEADD_REGION MASTER
LEFT JOIN PKP.T_SRV_VALUEADD_REGION_ORG MASTERORG
ON MASTER.ID = MASTERORG.T_SRV_PRICE_REGION_ID AND MASTERORG.ACTIVE = 'Y' 



	MASTER.ID = #{id}


	AND MASTER.CODE  = #{regionCode}


	AND MASTER.NAME like #{regionName}


 AND MASTER.BEGIN_TIME <= #{queryDate} AND MASTER.END_TIME > #{queryDate} 


	AND MASTER.TYPE = #{regionType}


	AND MASTER.ACTIVE  = #{active}


	AND MASTER.NATION_CODE = #{nationCode}


	AND MASTER.PROV_CODE = #{proCode}


	AND  MASTER.CITY_CODE = #{cityCode}


	AND MASTER.COUNTY_CODE like #{countyCode}


	AND MASTERORG.INCLUDE_ORG_CODE = #{deptCode}


	 AND MASTER.BEGIN_TIME <= #{beginTime} AND MASTER.END_TIME > #{beginTime} 



	 AND MASTERORG.BEGIN_TIME <= #{billDate} 
			  AND MASTERORG.END_TIME > #{billDate}

mapper.update:

	UPDATE /** 综合-价格-根据ID集合批量激活增值价格区域  **/ 
	pkp.t_srv_valueadd_region T
	SET T.ACTIVE = 'Y',
	T.VERSION_NO = #{versionNo,jdbcType=DECIMAL}


 T.ID IN 

		#{regionId}

DELETE /** 综合-价格-根据ID集合批量删除增值价格区域 **/ FROM pkp.t_srv_valueadd_region T
T.ID IN
#{regionId}

DELETE /** 综合-价格-根据ID集合批量删除增值价格区域和部门 **/ FROM pkp.t_srv_valueadd_region_org T
T.T_SRV_PRICE_REGION_ID IN
#{regionId}


	UPDATE /** 综合-价格-批量激活增值价格区域和部门  **/  
	pkp.t_srv_valueadd_region_org T
	SET T.ACTIVE = 'Y',
	T.VERSION_NO = #{versionNo,jdbcType=DECIMAL}


    T.T_SRV_PRICE_REGION_ID = #{regionId, jdbcType=VARCHAR}

mapper.insert:
INSERT INTO /** 综合-价格-插入增值价格区域 **/ pkp.t_srv_valueadd_region(
) values ( #{id, jdbcType=VARCHAR}, #{regionCode, jdbcType=VARCHAR}, #{regionName, jdbcType=VARCHAR}, #{regionType, jdbcType=VARCHAR}, #{active}, #{beginTime,jdbcType=TIMESTAMP}, #{endTime, jdbcType=TIMESTAMP}, #{description, jdbcType=VARCHAR}, #{versionNo, jdbcType=NUMERIC}, #{nationCode, jdbcType=VARCHAR}, #{proCode, jdbcType=VARCHAR}, #{cityCode, jdbcType=VARCHAR}, #{countyCode, jdbcType=VARCHAR}, #{createUser, jdbcType=VARCHAR}, #{modifyUser, jdbcType=VARCHAR}, sysdate, sysdate, #{createOrgCode, jdbcType=VARCHAR}, #{modifyOrgCode, jdbcType=VARCHAR}
)

mapper.select:

SELECT /** 综合-价格-根据条件查询增值区域关联部门  **/ 



FROM pkp.t_srv_valueadd_region_org MASTER



	MASTER.T_SRV_PRICE_REGION_ID = #{priceRegionId}


 AND MASTER.ACTIVE = #{active,jdbcType=VARCHAR}



 AND MASTER.INCLUDE_ORG_CODE = #{includeOrgCode,jdbcType=VARCHAR}



AND  MASTER.BEGIN_TIME   <=   #{billDate,jdbcType=TIMESTAMP} 
AND  MASTER.END_TIME     >    #{billDate,jdbcType=TIMESTAMP} 



ORDER BY MASTER.MODIFY_TIME DESC


SELECT /** 综合-价格-根据行政区域查询逻辑区域信息  **/ 



FROM pkp.t_srv_valueadd_region MASTER




	  MASTER.ACTIVE  = #{active}


	AND MASTER.TYPE = #{regionType}


	  AND (MASTER.COUNTY_CODE = #{countyCode}  or  MASTER.CITY_CODE = #{cityCode}  or MASTER.PROV_CODE = #{proCode})


	    AND ( MASTER.CITY_CODE = #{cityCode}  or MASTER.PROV_CODE = #{proCode})


	   AND MASTER.PROV_CODE = #{proCode}



	   AND MASTER.NATION_CODE = #{nationCode}




	 AND MASTER.BEGIN_TIME <= #{billDate,jdbcType=TIMESTAMP} AND MASTER.END_TIME > #{billDate,jdbcType=TIMESTAMP} 



ORDER BY MASTER.MODIFY_TIME DESC


SELECT /** 综合-价格-根据行政区域查询逻辑区域信息  **/ 



FROM pkp.t_srv_valueadd_region MASTER



	  MASTER.ACTIVE  = #{active}


	AND MASTER.TYPE = #{regionType}


	  AND (MASTER.COUNTY_CODE = #{countyCode}  or  MASTER.CITY_CODE = #{cityCode})


	    AND ( MASTER.CITY_CODE = #{cityCode} )


	   AND MASTER.NATION_CODE = #{nationCode}


	 AND MASTER.BEGIN_TIME <= #{billDate,jdbcType=TIMESTAMP} AND MASTER.END_TIME > #{billDate,jdbcType=TIMESTAMP} 



ORDER BY MASTER.MODIFY_TIME DESC


	SELECT /** 综合-价格-根据区域名称批量查找区域信息  **/  DISTINCT



	FROM pkp.t_srv_valueadd_region MASTER




		  MASTER.ACTIVE  = #{active}


      AND  MASTER.NAME IN 

		#{regionName}  



		 AND MASTER.BEGIN_TIME <= #{billDate} AND MASTER.END_TIME > #{billDate}


	SELECT /** 综合-价格-根据区域CODE批量查找区域信息  **/  DISTINCT



	FROM pkp.t_srv_valueadd_region MASTER



		  MASTER.ACTIVE  = #{active}


      AND  MASTER.CODE IN 

		#{regionCode}  



		 AND MASTER.BEGIN_TIME <= #{billDate} AND MASTER.END_TIME > #{billDate}


SELECT /** 综合-价格-根据区域CODE查找区域信息  **/



FROM pkp.t_srv_valueadd_region MASTER




	MASTER.ACTIVE = #{active}


	AND (MASTER.COUNTY_CODE = #{countyCode} or MASTER.CITY_CODE = #{cityCode}
	)


	AND ( MASTER.CITY_CODE = #{cityCode})


	 AND MASTER.BEGIN_TIME <= #{billDate,jdbcType=TIMESTAMP} AND MASTER.END_TIME > #{billDate,jdbcType=TIMESTAMP}

SELECT /** 综合-价格-网点CODE查询价格区域关联部门 **/
FROM pkp.t_srv_valueadd_region_org MASTER
AND MASTER.ACTIVE = 'Y' AND MASTER.INCLUDE_ORG_CODE = #{deptNo,jdbcType=VARCHAR}


SELECT /** 综合-价格-根据综合条件查询价格区域 **/ DISTINCT



FROM pkp.t_srv_valueadd_region T
LEFT JOIN pkp.t_srv_valueadd_region_org MASTER
ON T.ID = MASTER.T_SRV_PRICE_REGION_ID



	T.ID = #{id}


	 AND MASTER.BEGIN_TIME <= #{billDate} AND MASTER.END_TIME > #{billDate}


SELECT /** 综合-价格-根据综合条件查询价格区域 **/ 



FROM pkp.t_srv_valueadd_region_org MASTER



	MASTER.ID = #{id}
