mapper.update:
UPDATE PKP.T_SRV_ACTUAL_FREIGHT
ARRIVE_NOTOUT_GOODS_QTY = DECODE(SIGN(ARRIVE_NOTOUT_GOODS_QTY - #{arriveNotoutGoodsQty,jdbcType=DECIMAL}), 1, ARRIVE_NOTOUT_GOODS_QTY - #{arriveNotoutGoodsQty,jdbcType=DECIMAL}, 0),
PAYMENT_TYPE = #{paymentType,jdbcType=VARCHAR},
MODIFY_TIME=#{modifyTime,jdbcType=TIMESTAMP},
WHERE WAYBILL_NO = #{waybillNo,jdbcType=VARCHAR}
and ARRIVE_NOTOUT_GOODS_QTY = #{oldArriveNotoutGoodsQty,jdbcType=DECIMAL}
and status not in
#{status,jdbcType=VARCHAR}

mapper.select:
    	SELECT /*模块：接送货-运单-根据运单号、运单类型进行查询*/

		FROM PKP.T_SRV_ACTUAL_FREIGHT WHERE WAYBILL_TYPE=#{waybillNo,jdbcType=VARCHAR}
    	AND WAYBILL_NO=#{waybillNo,jdbcType=VARCHAR} ORDER BY CREATE_TIME DESC

