mapper.select:
select /*模块：接送货-运单-通过主键查询*/

from PKP.T_SRV_LABELED_GOOD
where ID = #{id,jdbcType=VARCHAR}

    select /*模块：接送货-运单-通过运单号和流水号查询*/

    from PKP.T_SRV_LABELED_GOOD
    where WAYBILL_NO = #{waybillNo,jdbcType=VARCHAR}
    	and SERIAL_NO = #{serialNo,jdbcType=VARCHAR}
	    and ACTIVE = #{active,jdbcType=CHAR}

    select /*模块：接送货-运单-通过运单号和流水号查询(为中转提供)*/

    from PKP.T_SRV_LABELED_GOOD
    where 1=1

	       and WAYBILL_NO = #{waybillNo,jdbcType=VARCHAR}


	       and SERIAL_NO = #{serialNo,jdbcType=VARCHAR}


	       and ACTIVE = #{active,jdbcType=VARCHAR}

select /*模块：接送货-运单-通过运单号查询最大的序列号*/

from PKP.T_SRV_LABELED_GOOD
where WAYBILL_NO = #{waybillNo,jdbcType=VARCHAR}

	AND ACTIVE = #{active,jdbcType=VARCHAR}

order by serial_no asc

mapper.update:
	 	UPDATE  PKP.T_SRV_LABELED_GOOD
	 		SET ACTIVE = 'N' 
 		WHERE WAYBILL_NO=#{waybillNo,jdbcType=VARCHAR}
 		AND SERIAL_NO=#{serialNo,jdbcType=VARCHAR}

	 	UPDATE PKP.T_SRV_LABELED_GOOD
	 	 SET ACTIVE =  #{active,jdbcType=CHAR}
 		 WHERE WAYBILL_NO=#{waybillNo,jdbcType=VARCHAR} AND ACTIVE =  #{conActive,jdbcType=CHAR}

mapper.select:
 	select /*模块：接送货-运单-通过运单号和流水号集合查询*/

    from PKP.T_SRV_LABELED_GOOD


	       and WAYBILL_NO = #{waybillNo,jdbcType=VARCHAR}


	       and ACTIVE = #{active,jdbcType=VARCHAR}



	    	and SERIAL_NO in


		    	      	#{labeledGoodId,jdbcType=VARCHAR} 




	    		and SERIAL_NO = ''

mapper.insert:
begin
insert into PKP.T_SRV_LABELED_GOOD (ID, SERIAL_NO, ACTIVE, WAYBILL_NO, CREATE_TIME, MODIFY_TIME, BILL_TIME, OLD_SERIAL_NO, NUMBER_CHANG_ITEMS, INITIAL_VALUE, IS_NEED_WOODEN,Package_type) values (#{item.id,jdbcType=VARCHAR}, #{item.serialNo,jdbcType=VARCHAR}, #{item.active,jdbcType=CHAR}, #{item.waybillNo,jdbcType=VARCHAR}, #{item.createTime,jdbcType=TIMESTAMP}, #{item.modifyTime,jdbcType=TIMESTAMP}, #{item.billTime,jdbcType=TIMESTAMP}, #{item.oldSerialNo,jdbcType=VARCHAR}, #{item.numberChangItems,jdbcType=VARCHAR}, #{item.initalVale,jdbcType=CHAR}, #{item.isNeedWooden,jdbcType=CHAR}, #{item.packageType,jdbcType=VARCHAR})
;end;

mapper.delete:
	    delete from  PKP.T_SRV_LABELED_GOOD
		       where WAYBILL_NO in

                 #{item}
