mapper.select:
select /** 根据运单号或者是订单号查询OMS订单**/

from PKP.T_SRV_OMS_ORDER 
where 1 = 1 

	and order_No = #{orderNo,jdbcType=VARCHAR}


	and waybill_No = #{waybillNo,jdbcType=VARCHAR}

select /** 根据运单号，订单号，或者发货客户编码运单状态等查询订单信息和运单状态**/ o.waybill_status waybillStatus, p.operate_result labelPushStatus, o.waybill_no waybillNo, o.order_no orderNo, decode(w.receive_org_name,null, o.income_org_name,w.receive_org_name) receiveOrgName, decode(w.create_org_code,null,o.income_org_name,(select og.name from BSE.T_BAS_ORG OG where og.code = w.create_org_code and og.active = 'Y')) createOrgName, decode(w.customer_pickup_org_code,null,(select og.name from BSE.T_BAS_ORG OG where og.code = o.customer_pickup_org_code and og.active = 'Y'),(select og.name from BSE.T_BAS_ORG OG where og.code = w.customer_pickup_org_code and og.active = 'Y')) as customerPickupOrgName, O.CREATE_TIME billTime, decode(i.scan,'Y', i.modify_time,'') as scanTime, o.delivery_customer_code deliveryCustomerCode, o.delivery_customer_contact deliveryCustomerContact, o.fail_reason failReason from PKP.T_SRV_OMS_ORDER O left join pkp.t_srv_waybill w on w.waybill_no = o.waybill_no and w.active = 'Y' left join pkp.t_srv_pda_app_info i on i.waybill_no = o.waybill_no left join pkp.t_srv_label_push_process p on p.waybill_no = o.waybill_no and p.active = 'Y', BSE.T_BAS_ORG ORG where ORG.ACTIVE = 'Y' and i.active = 'Y' and org.code = decode(w.RECEIVE_ORG_CODE,null,o.income_org_code,w.RECEIVE_ORG_CODE) and org.code = #{createOrgCode,jdbcType=VARCHAR}
AND O.WAYBILL_NO IN
#{waybillNo,jdbcType=VARCHAR}
AND O.ORDER_NO IN
#{orderNo,jdbcType=VARCHAR}
AND O.DELIVERY_CUSTOMER_CODE IN
#{customerCode,jdbcType=VARCHAR}
AND O.WAYBILL_STATUS = #{waybillStatus,jdbcType=TIMESTAMP}
AND p.operate_result = #{labelPushStatus,jdbcType=TIMESTAMP}
AND O.CREATE_TIME >= #{startTime,jdbcType=TIMESTAMP}
AND O.CREATE_TIME <= #{endTime,jdbcType=TIMESTAMP}
order by o.waybill_status desc
