mapper.select:
select

from PKP.T_SRV_WAYBILL_PENDING
where ID = #{id,jdbcType=VARCHAR}

select /*模块：接送货-运单-查询运单待处理表*/

from PKP.T_SRV_WAYBILL_PENDING

	ACTIVE = #{active,jdbcType=VARCHAR}

		AND BILL_TIME   >=  
		#{pendingDto.billStartTime} and BILL_TIME   <=  
		#{pendingDto.billEndTime}


		and CREATE_TIME   >=  
		#{pendingDto.createStartTime}
		and CREATE_TIME   <=  
		#{pendingDto.createEndTime}


		and (receive_org_code =
		#{pendingDto.waybillPending.receiveOrgCode,jdbcType=VARCHAR}

		)


		and ( receive_org_code in

			#{receiveOrgCode, jdbcType=VARCHAR}

		or create_org_code in

			#{receiveOrgCode, jdbcType=VARCHAR}

		)


		and product_code =
		#{pendingDto.waybillPending.productCode,jdbcType=VARCHAR}



		and product_code IN ('PACKAGE','RCP','EPEP','DEAP')


		and pending_type =
		#{pendingDto.waybillPending.pendingType,jdbcType=VARCHAR}



		and IS_BATCH_CREATE_WAYBILL =
		#{pendingDto.waybillPending.isBatchCreateWaybill,jdbcType=VARCHAR}



		and create_user_code =
		#{pendingDto.waybillPending.createUserCode,jdbcType=VARCHAR}


		and create_org_code =
		#{pendingDto.waybillPending.createOrgCode,jdbcType=VARCHAR}


		and create_org_code in

			#{createOrgCode, jdbcType=VARCHAR}



		and WAYBILL_NO = #{pendingDto.mixNo,jdbcType=VARCHAR}


		and ORDER_NO =#{pendingDto.orderNo,jdbcType=VARCHAR}


		and WAYBILL_TYPE
		=#{pendingDto.waybillType,jdbcType=VARCHAR}


order by BILL_TIME asc

select /*模块：接送货-运单-查询运单待处理表*/ p.ID, p.WAYBILL_NO, p.ORDER_NO, p.ORDER_CHANNEL, p.ORDER_PAID_METHOD, p.DELIVERY_CUSTOMER_ID, p.DELIVERY_CUSTOMER_CODE, p.DELIVERY_CUSTOMER_NAME, p.DELIVERY_CUSTOMER_MOBILEPHONE, p.DELIVERY_CUSTOMER_PHONE, p.DELIVERY_CUSTOMER_CONTACT, p.DELIVERY_CUSTOMER_NATION_CODE, p.DELIVERY_CUSTOMER_PROV_CODE, p.DELIVERY_CUSTOMER_CITY_CODE, p.DELIVERY_CUSTOMER_DIST_CODE, p.DELIVERY_CUSTOMER_ADDRESS, p.RECEIVE_CUSTOMER_ID, p.RECEIVE_CUSTOMER_CODE, p.RECEIVE_CUSTOMER_NAME, p.RECEIVE_CUSTOMER_MOBILEPHONE, p.RECEIVE_CUSTOMER_PHONE, p.RECEIVE_CUSTOMER_CONTACT, p.RECEIVE_CUSTOMER_NATION_CODE, p.RECEIVE_CUSTOMER_PROV_CODE, p.RECEIVE_CUSTOMER_CITY_CODE, p.RECEIVE_CUSTOMER_DIST_CODE, p.RECEIVE_CUSTOMER_ADDRESS, p.RECEIVE_ORG_CODE, p.PRODUCT_ID, p.PRODUCT_CODE, p.RECEIVE_METHOD, p.CUSTOMER_PICKUP_ORG_CODE, p.LOAD_METHOD, p.TARGET_ORG_CODE, p.PICKUP_TO_DOOR, p.DRIVER_CODE, p.PICKUP_CENTRALIZED, p.LOAD_LINE_CODE, p.LOAD_ORG_CODE, p.LAST_LOAD_ORG_CODE, p.PRE_DEPARTURE_TIME, p.PRE_CUSTOMER_PICKUP_TIME, p.CAR_DIRECT_DELIVERY, p.GOODS_NAME, p.GOODS_QTY_TOTAL,p.GOODS_WEIGHT_TOTAL, p.GOODS_VOLUME_TOTAL, p.GOODS_SIZE, p.GOODS_TYPE_CODE, p.PRECIOUS_GOODS, p.SPECIAL_SHAPED_GOODS, p.OUTER_NOTES, p.INNER_NOTES, p.GOODS_PACKAGE, p.INSURANCE_AMOUNT / 100 as INSURANCE_AMOUNT, p.INSURANCE_RATE, p.INSURANCE_FEE / 100 as INSURANCE_FEE, p.COD_AMOUNT / 100 as COD_AMOUNT, p.COD_RATE, p.COD_FEE / 100 as COD_FEE, p.REFUND_TYPE, p.RETURN_BILL_TYPE, p.SECRET_PREPAID, p.TO_PAY_AMOUNT / 100 as TO_PAY_AMOUNT, p.PRE_PAY_AMOUNT / 100 as PRE_PAY_AMOUNT, p.DELIVERY_GOODS_FEE / 100 as DELIVERY_GOODS_FEE, p.OTHER_FEE / 100 as OTHER_FEE, p.PACKAGE_FEE / 100 as PACKAGE_FEE, p.PROMOTIONS_FEE / 100 as PROMOTIONS_FEE, p.BILLING_TYPE, p.UNIT_PRICE / 100 AS UNIT_PRICE, p.TRANSPORT_FEE / 100 as TRANSPORT_FEE, p.VALUE_ADD_FEE / 100 as VALUE_ADD_FEE, p.PAID_METHOD, p.ARRIVE_TYPE, p.ACTIVE, p.FORBIDDEN_LINE, p.FREIGHT_METHOD, p.FLIGHT_SHIFT, p.TOTAL_FEE / 100 as TOTAL_FEE, p.PROMOTIONS_CODE, p.CREATE_TIME, p.MODIFY_TIME, p.BILL_TIME, p.CREATE_USER_CODE, p.MODIFY_USER_CODE, p.CREATE_ORG_CODE, p.MODIFY_ORG_CODE, p.REF_ID, p.REF_CODE, p.CURRENCY_CODE, p.IS_WHOLE_VEHICLE, p.WHOLE_VEHICLE_APPFEE / 100 as WHOLE_VEHICLE_APPFEE, p.WHOLE_VEHICLE_ACTUALFEE / 100 as WHOLE_VEHICLE_ACTUALFEE, p.ACCOUNT_NAME, p.ACCOUNT_CODE, p.ACCOUNT_BANK, p.BILL_WEIGHT, p.PICKUP_FEE / 100 as PICKUP_FEE, p.SERVICE_FEE / 100 as SERVICE_FEE, p.PRE_ARRIVE_TIME, p.ADD_TIME, p.PENDING_TYPE, p.RECEIVE_CUSTOMER_CONTACT_ID, p.PAPER_NUM, p.WOOD_NUM, p.FIBRE_NUM, p.SALVER_NUM, p.MEMBRANE_NUM, p.OTHER_PACKAGE, p.DELIVERY_CUSTOMER_CONTACT_ID, p.TRANSPORT_TYPE, p.TRANSPORTATION_REMARK, p.CONTACT_ADDRESS_ID, p.FLIGHT_NUMBER_TYPE, p.IS_PASS_OWN_DEPARTMENT, p.COLLECTION_DEPARTMENT, p.LICENSE_PLATE_NUM, p.HANDOVER_STATUS, p.IS_OUTER_BRANCH, p.ORDER_VEHICLE_NUM, p.kilometer, p.IS_ADD_CODE, p.ADD_CODE_TIME, p.RETURN_TYPE, p.ORIGINAL_WAYBILL_NO, p.EXPRESS_EMP_CODE, p.EXPRESS_EMP_NAME, p.EXPRESS_ORG_CODE, p.EXPRESS_ORG_NAME, p.PDA_SERIAL, p.BANK_TRADE_SERIAL, p.isSMS, p.special_offer_NO, p.waybill_type, p.IS_EXPRESS, p.PACKAGE_REMARK, p.INVOICE, p.BUSINESS_ZONE, p.RESIDENTIAL_DISTRICT, p.SERVICE_RATE from PKP.T_SRV_WAYBILL_PENDING p
p.ACTIVE = #{active,jdbcType=VARCHAR} and p.waybill_no not in (select pic.waybill_no from pkp.t_srv_pic_waybill_pending pic where p.waybill_no = pic.waybill_no and pic.pendging_type <> 'PDA_ACTIVE' and pic.active = 'Y')
AND p.BILL_TIME >= #{pendingDto.billStartTime} and p.BILL_TIME <= #{pendingDto.billEndTime}
and p.CREATE_TIME >= #{pendingDto.createStartTime} and p.CREATE_TIME <= #{pendingDto.createEndTime}
and (p.receive_org_code = #{pendingDto.waybillPending.receiveOrgCode,jdbcType=VARCHAR}
)
and ( p.receive_org_code in
#{receiveOrgCode, jdbcType=VARCHAR}
or p.create_org_code in
#{receiveOrgCode, jdbcType=VARCHAR}
)
and p.product_code = #{pendingDto.waybillPending.productCode,jdbcType=VARCHAR}
and p.product_code NOT IN('PACKAGE','RCP','EPEP')
and p.pending_type = #{pendingDto.waybillPending.pendingType,jdbcType=VARCHAR}
and p.create_user_code = #{pendingDto.waybillPending.createUserCode,jdbcType=VARCHAR}
and (p.create_org_code = #{pendingDto.waybillPending.createOrgCode,jdbcType=VARCHAR} or (p.RECEIVE_ORG_CODE = #{pendingDto.waybillPending.createOrgCode,jdbcType=VARCHAR} AND EXISTS (SELECT 1 FROM PKP.T_SRV_OMS_ORDER IT WHERE IT.WAYBILL_NO = p.WAYBILL_NO)))
and p.create_org_code in
#{createOrgCode, jdbcType=VARCHAR}
and p.WAYBILL_NO = #{pendingDto.mixNo,jdbcType=VARCHAR}
and p.ORDER_NO =#{pendingDto.orderNo,jdbcType=VARCHAR}
order by p.BILL_TIME asc

