WHERE P.ACTIVE
=#{active,jdbcType=VARCHAR}

	and P.WAYBILL_NO = #{waybillNo,jdbcType=VARCHAR}


	and P.ORDER_NO = #{orderNo,jdbcType=VARCHAR}

select /*模块：接送货-运单-根据运单号获取待补录信息*/

from PKP.T_SRV_WAYBILL_PENDING

	ACTIVE = #{active,jdbcType=VARCHAR}

		and WAYBILL_NO = #{waybillNo,jdbcType=VARCHAR}

select /*模块：接送货-运单-根据运单号、包装信息查询待补录的快递单*/

from PKP.T_SRV_WAYBILL_PENDING


		and WAYBILL_NO = #{waybillNo,jdbcType=VARCHAR}


		and product_code = #{productCode,jdbcType=VARCHAR}


	 and product_code IN('PACKAGE','RCP','EPEP')

