SELECT /*模块：接送货-运单-从运单表中查询电子运单中待激活运单数据*/ /* PEND.CREATE_ORG_CODE originDeptCode, PEND.CUSTOMER_PICKUP_ORG_CODE destDeptCode, PEND.GOODS_QTY_TOTAL pieces, PEND.GOODS_WEIGHT_TOTAL/100 weight, PEND.GOODS_VOLUME_TOTAL/100 volume, PEND.OUTER_NOTES remark, PEND.INSURANCE_AMOUNT/100 insuredAmount, PEND.REFUND_TYPE reciveLoanType, PEND.COD_AMOUNT/100 reviceMoneyAmount, PEND.ACCOUNT_CODE reviceLoadAccount, PEND.RETURN_BILL_TYPE returnBillingType, PEND.CREATE_USER_CODE userCode, PEND.DRIVER_CODE truckCode, PEND.WAYBILL_NO waybillCode, ORD.DEVICE_NO pdaCode, ORD.ORDER_NO orderCode, ORD.EARLIEST_PICKUP_TIME firstAcctTime, ORD.LATEST_PICKUP_TIME lastAcctTime, DECODE(PEND.WAYBILL_TYPE,'EWAYBILL','E','N') orderType, PEND.PRODUCT_CODE transType, PEND.RECEIVE_METHOD takeType, PEND.PAID_METHOD payType, PEND.TRANSPORT_FEE payType, ORD.RECEIVE_ORG_CODE revenueCode, PEND.PROMOTIONS_CODE couponNum, ORD.IS_COLLECT isCollectGps, PEND.BILL_TIME billingTime, PEND.BANK_TRADE_SERIAL bankTradeSerail, PEND.RECEIVE_CUSTOMER_CONTACT addresseeName, PEND.ORDER_CHANNEL channelCode, PEND.GOODS_NAME goodsName, PEND.RECEIVE_CUSTOMER_MOBILEPHONE receiveMobilePhone, PEND.RECEIVE_CUSTOMER_PHONE receivePhone, PEND.RECEIVE_CUSTOMER_PROV_CODE receiveProCode, PEND.RECEIVE_CUSTOMER_CITY_CODE receiveCityCode, PEND.RECEIVE_CUSTOMER_DIST_CODE receiveDistCode, PEND.RECEIVE_CUSTOMER_ADDRESS receiveAddress, PEND.DELIVERY_CUSTOMER_CONTACT senderName, PEND.DELIVERY_CUSTOMER_MOBILEPHONE deliveryMobilePhone, PEND.DELIVERY_CUSTOMER_PHONE deliveryPhone, PEND.DELIVERY_CUSTOMER_PROV_CODE deliveryProCode, PEND.DELIVERY_CUSTOMER_CITY_CODE deliveryCityCode, PEND.DELIVERY_CUSTOMER_DIST_CODE deliveryDistCode, PEND.DELIVERY_CUSTOMER_ADDRESS deliveryAddress */ PEND.GOODS_QTY_TOTAL pieces, PEND.RETURN_BILL_TYPE returnBillingType, PEND.WAYBILL_NO waybillCode, ORD.ORDER_NO orderCode FROM PKP.T_SRV_WAYBILL_PENDING PEND LEFT JOIN PKP.T_SRV_DISPATCH_ORDER ORD ON ORD.ORDER_NO=PEND.ORDER_NO AND PEND.ACTIVE='N' LEFT JOIN PKP.T_SRV_ACTUAL_FREIGHT AC ON AC.WAYBILL_NO = PEND.WAYBILL_NO WHERE PEND.PENDING_TYPE='EWAYBILL_PRE_ACTIVE' AND PEND.WAYBILL_TYPE='EWAYBILL' AND PEND.BILL_TIME >TRUNC(SYSDATE)-7 AND AC.STATUS='UNACTIVE'
AND ORD.DRIVER_CODE=#{driverCode,jdbcType=VARCHAR}
AND ORD.VEHICLE_NO=#{vehicleNo,jdbcType=VARCHAR}
AND TRIM(PEND.DELIVERY_CUSTOMER_ADDRESS)=TRIM(#{address,jdbcType=VARCHAR})
/*若手机和固定电话都不为空，则都作为条件查询*/ AND PEND.DELIVERY_CUSTOMER_MOBILEPHONE=TRIM(#{mobilePhone,jdbcType=VARCHAR}) AND PEND.DELIVERY_CUSTOMER_PHONE =TRIM(#{officePhone,jdbcType=VARCHAR})
/*若手机和固定电话有一个为空（都会手机号或者固话都会以mobilePhone参数传入）*/ AND ( (PEND.DELIVERY_CUSTOMER_MOBILEPHONE=TRIM(#{mobilePhone,jdbcType=VARCHAR}) and PEND.DELIVERY_CUSTOMER_PHONE is null) or (PEND.DELIVERY_CUSTOMER_PHONE =TRIM(#{mobilePhone,jdbcType=VARCHAR}) and PEND.DELIVERY_CUSTOMER_MOBILEPHONE is null) )
AND ORD.ORDER_STATUS IN
#{orderStatus,jdbcType=VARCHAR}
AND PEND.DELIVERY_CUSTOMER_CONTACT_ID IN
#{customerCode,jdbcType=VARCHAR}
AND PEND.DELIVERY_CUSTOMER_CONTACT_ID IS NOT NULL
ORDER BY PEND.DELIVERY_CUSTOMER_CODE

SELECT CON.CODE contactCode, CON.CONTACT_PHONE officePhone, CON.MOBILE_PHONE mobilePhone, CON.CONTACT_NAME contactName, ADDR.CONTACT_ADDRESS address, CU.CODE customerCode, CU.NAME customerName FROM BSE.T_BAS_CUS_CONTACT CON LEFT JOIN BSE.T_BAS_CUS_CONTACT_ADDRESS ADDR ON ADDR.CONTACT_CRM_ID = CON.CRM_ID AND ADDR.ACTIVE = 'Y' LEFT JOIN BSE.T_BAS_CUSTOMER CU ON CU.CRM_CUS_ID = CON.CRM_CUS_ID AND CU.ACTIVE='Y'
AND CON.CRM_ID = #{custContactCode,jdbcType=VARCHAR}
AND CON.CRM_ID = ''

mapper.sql:
from pkp.t_srv_waybill_pending pending /*模块：接送货-电子运单查询-官网查询打印待激活电子运单数量*/ inner join pkp.t_srv_actual_freight freight on freight.waybill_no=pending.waybill_no inner join bse.t_bas_org org on org.code = pending.CUSTOMER_PICKUP_ORG_CODE AND ORG.ACTIVE='Y' inner join PKP.T_SRV_DISPATCH_ORDER ORD ON ORD.ORDER_NO = PENDING.ORDER_NO where pending.active = #{active,jdbcType=VARCHAR} and pending.waybill_type= #{waybillType,jdbcType=VARCHAR} AND ORD.ORDER_STATUS IN('DISPATCH_VEHICLE','PDA_ACCEPT','PICKUPING','AGAIN_PICKUP')
and ( pending.delivery_customer_contact_id = #{linkmanId,jdbcType=VARCHAR}
or freight.CHANNELCUSTID = #{userName,jdbcType=VARCHAR}
)
and pending.waybill_no =#{waybillNO,jdbcType=VARCHAR}
and pending.order_no =#{orderNo,jdbcType=VARCHAR}
and pending.DELIVERY_CUSTOMER_CONTACT like CONCAT(#{deliveryCustomerName,jdbcType=VARCHAR},'%')
and pending.RECEIVE_CUSTOMER_NAME like CONCAT(#{receiveCustomerName,jdbcType=VARCHAR},'%')
and pending.goods_name like CONCAT(#{goodsName,jdbcType=VARCHAR},'%')
and pending.create_time between #{startDate,jdbcType=TIMESTAMP} and #{endDate,jdbcType=TIMESTAMP}
AND NOT EXISTS (SELECT 1 FROM PKP.T_SRV_WAYBILL W WHERE W.WAYBILL_NO = PENDING.WAYBILL_NO AND W.ACTIVE='Y' AND W.PENDING_TYPE IN ('PC_ACTIVE', 'PDA_ACTIVE')) order by pending.create_time desc

mapper.select:
SELECT /*模块：接送货-运单-从运单表中查询电子运单中待激活运单数据*/ PEND.CREATE_ORG_CODE originDeptCode, PEND.CUSTOMER_PICKUP_ORG_CODE destDeptCode, PEND.GOODS_QTY_TOTAL pieces, PEND.GOODS_WEIGHT_TOTAL/100 weight, PEND.GOODS_VOLUME_TOTAL/100 volume, PEND.OUTER_NOTES remark, PEND.INSURANCE_AMOUNT/100 insuredAmount, PEND.REFUND_TYPE reciveLoanType, PEND.COD_AMOUNT/100 reviceMoneyAmount, PEND.ACCOUNT_CODE reviceLoadAccount, PEND.RETURN_BILL_TYPE returnBillingType, PEND.CREATE_USER_CODE userCode, PEND.DRIVER_CODE truckCode, PEND.WAYBILL_NO waybillCode, ORD.DEVICE_NO pdaCode, ORD.ORDER_NO orderCode, ORD.EARLIEST_PICKUP_TIME firstAcctTime, ORD.LATEST_PICKUP_TIME lastAcctTime, DECODE(PEND.WAYBILL_TYPE,'EWAYBILL','E','N') orderType, PEND.PRODUCT_CODE transType, PEND.RECEIVE_METHOD takeType, PEND.PAID_METHOD payType, PEND.TRANSPORT_FEE payType, ORD.RECEIVE_ORG_CODE revenueCode, PEND.PROMOTIONS_CODE couponNum, ORD.IS_COLLECT isCollectGps, PEND.BILL_TIME billingTime, PEND.BANK_TRADE_SERIAL bankTradeSerail, PEND.RECEIVE_CUSTOMER_CONTACT addresseeName, PEND.ORDER_CHANNEL channelCode, PEND.GOODS_NAME goodsName, PEND.RECEIVE_CUSTOMER_MOBILEPHONE receiveMobilePhone, PEND.RECEIVE_CUSTOMER_PHONE receivePhone, PEND.RECEIVE_CUSTOMER_PROV_CODE receiveProCode, PEND.RECEIVE_CUSTOMER_CITY_CODE receiveCityCode, PEND.RECEIVE_CUSTOMER_DIST_CODE receiveDistCode, PEND.RECEIVE_CUSTOMER_ADDRESS receiveAddress, PEND.DELIVERY_CUSTOMER_CONTACT senderName, PEND.DELIVERY_CUSTOMER_MOBILEPHONE deliveryMobilePhone, PEND.DELIVERY_CUSTOMER_PHONE deliveryPhone, PEND.DELIVERY_CUSTOMER_PROV_CODE deliveryProCode, PEND.DELIVERY_CUSTOMER_CITY_CODE deliveryCityCode, PEND.DELIVERY_CUSTOMER_DIST_CODE deliveryDistCode, PEND.DELIVERY_CUSTOMER_ADDRESS deliveryAddress FROM PKP.T_SRV_WAYBILL_PENDING PEND LEFT JOIN PKP.T_SRV_DISPATCH_ORDER ORD ON ORD.ORDER_NO=PEND.ORDER_NO AND PEND.ACTIVE='N' WHERE PEND.PENDING_TYPE='EWAYBILL_PRE_ACTIVE' AND PEND.WAYBILL_TYPE='EWAYBILL' AND PEND.BILL_TIME >TRUNC(SYSDATE)-7
AND ORD.DRIVER_CODE=#{driverCode,jdbcType=VARCHAR}
AND ORD.VEHICLE_NO=#{vehicleNo,jdbcType=VARCHAR}
AND ORD.ORDER_STATUS IN
#{orderStatus,jdbcType=VARCHAR}
AND PEND.DELIVERY_CUSTOMER_CODE IN
#{customerCode,jdbcType=VARCHAR}
AND PEND.DELIVERY_CUSTOMER_CODE IS NULL
ORDER BY PEND.DELIVERY_CUSTOMER_CODE

SELECT /*模块：接送货-运单-根据运单号查询待激活运单数据详情*/

FROM PKP.T_SRV_WAYBILL_PENDING


		AND WAYBILL_NO=#{waybillNo,jdbcType=VARCHAR}


		AND ORDER_NO=#{orderNo,jdbcType=VARCHAR}


		AND PENDING_TYPE=#{pendingType,jdbcType=VARCHAR}


		AND ACTIVE=#{active,jdbcType=VARCHAR}


		AND WAYBILL_TYPE=#{waybillType,jdbcType=VARCHAR}


ORDER BY CREATE_TIME

SELECT /*模块：接送货-运单-根据运单号查询待激活运单数据详情*/

FROM PKP.T_SRV_WAYBILL_PENDING


		AND ORDER_NO=#{orderNo,jdbcType=VARCHAR}


		AND PENDING_TYPE=#{pendingType,jdbcType=VARCHAR}


		AND ACTIVE=#{active,jdbcType=VARCHAR}


		AND WAYBILL_TYPE=#{waybillType,jdbcType=VARCHAR}


ORDER BY CREATE_TIME

SELECT P.WAYBILL_NO WAYBILL_NO, P.ORDER_NO ORDER_NO, P.CREATE_ORG_CODE CREATE_ORG_CODE, P.RECEIVE_ORG_CODE RECEIVE_ORG_CODE, P.BILL_TIME BILL_TIME, P.DELIVERY_CUSTOMER_CODE DELIVERY_CUSTOMER_CODE, P.DELIVERY_CUSTOMER_CONTACT DELIVERY_CUSTOMER_CONTACT, P.RECEIVE_CUSTOMER_CONTACT RECEIVE_CUSTOMER_CONTACT, P.DELIVERY_CUSTOMER_CITY_CODE DELIVERY_CUSTOMER_CITY_CODE, P.CUSTOMER_PICKUP_ORG_CODE CUSTOMER_PICKUP_ORG_CODE, P.CREATE_USER_CODE CREATE_USER_CODE, P.ORDER_CHANNEL ORDER_CHANNEL, P.GOODS_WEIGHT_TOTAL GOODS_WEIGHT_TOTAL, P.DRIVER_CODE DRIVER_CODE, P.GOODS_VOLUME_TOTAL GOODS_VOLUME_TOTAL, P.GOODS_QTY_TOTAL GOODS_QTY_TOTAL, P.PRODUCT_CODE PRODUCT_CODE, P.PAID_METHOD PAID_METHOD, P.TOTAL_FEE / 100 TOTAL_FEE, P.COD_AMOUNT / 100 COD_AMOUNT, O.WEIGHT WEIGHT, O.VOLUME VOLUME, O.GOODS_QTY GOODS_QTY, A.STATUS WAYBILL_STATUS, A.GOODS_QTY GOODS_QTY, A.WEIGHT WEIGHT, A.VOLUME VOLUME, SCSNN.SCAN_TIME SCAN_TIME, EW.FAIL_REASON FAIL_REASON FROM PKP.T_SRV_WAYBILL_PENDING P LEFT JOIN PKP.T_SRV_ACTUAL_FREIGHT A ON A.WAYBILL_NO=P.WAYBILL_NO AND P.WAYBILL_TYPE=A.WAYBILL_TYPE LEFT JOIN PKP.T_SRV_DISPATCH_ORDER O ON O.ORDER_NO = P.ORDER_NO LEFT JOIN PKP.T_SRV_EWAYBILL_ORDER EW ON O.ORDER_NO=EW.ORDER_NO LEFT JOIN (SELECT DISTINCT S.WAYBILL_NO WAYBILL_NO,MIN(S.SCAN_TIME) SCAN_TIME FROM PKP.T_SRV_PDA_SCAN S WHERE S.ACTIVE = 'Y' AND S.SCAN_TIME >= to_date(to_char(sysdate,'yyyy-mm-dd ') || '00:00:00','yyyy-mm-dd hh24:mi:ss') AND S.SCAN_TIME <= to_date(to_char(sysdate,'yyyy-mm-dd ') || '23:59:59','yyyy-mm-dd hh24:mi:ss')
AND S.WAYBILL_TYPE = #{waybillType,jdbcType=VARCHAR}
AND S.SCAN_TYPE = #{scanType,jdbcType=VARCHAR}
AND S.TASK_TYPE = #{taskType,jdbcType=VARCHAR}
AND S.WHETHER_COMPLETE = #{whetherComplete,jdbcType=VARCHAR}
AND S.CREATE_ORG_CODE = #{currentDeptCode,jdbcType=VARCHAR}
AND S.WAYBILL_NO IN
#{eWaybillNo,jdbcType=VARCHAR}
AND S.ORDER_NO IN
#{orderNo,jdbcType=VARCHAR}
AND S.DRIVER_CODE IN
#{driverCode,jdbcType=VARCHAR}
GROUP BY S.WAYBILL_NO) SCSNN ON SCSNN.WAYBILL_NO = EW.WAYBILL_NO WHERE 1=1
AND (P.RECEIVE_ORG_CODE=#{currentDeptCode,jdbcType=VARCHAR} OR P.CREATE_ORG_CODE=#{currentDeptCode,jdbcType=VARCHAR})
AND P.WAYBILL_TYPE = #{waybillType,jdbcType=VARCHAR}
AND P.ACTIVE = #{active,jdbcType=VARCHAR}
AND P.WAYBILL_NO IN
#{eWaybillNo,jdbcType=VARCHAR}
AND P.ORDER_NO IN
#{orderNo,jdbcType=VARCHAR}
AND P.DELIVERY_CUSTOMER_CODE IN
#{customerCode,jdbcType=VARCHAR}
AND O.ORDER_TIME >= #{billStartTime,jdbcType=TIMESTAMP}
AND O.ORDER_TIME <= #{billEndTime,jdbcType=TIMESTAMP}
AND P.PENDING_TYPE IN
#{waybillStatus,jdbcType=VARCHAR}
AND NOT EXISTS( SELECT 1 FROM PKP.T_SRV_WAYBILL W WHERE W.WAYBILL_NO = P.WAYBILL_NO AND W.ACTIVE='Y')

SELECT /*模块：接送货-运单-根据运单号查询激活失败的运单数据详情*/

FROM PKP.T_SRV_WAYBILL_PENDING


		AND ACTIVE=#{active,jdbcType=VARCHAR}


		AND (ORDER_NO=#{mixNo,jdbcType=VARCHAR} OR
