mapper.select:
    select /*结算-凭证报表-根据条件查询始发月报表*/

    from STV.T_STL_MVR_NRFONT t

    	t.PERIOD= #{period}



    and t.PRODUCT_CODE in

	   	#{productCode,jdbcType=VARCHAR}




		AND t.CUSTOMER_CODE=#{customerCode}





	   and exists (select 1
         from bse.mv_bas_user_org_auth_view uov
