mapper.select:
    select /*结算-凭证报表-根据条件查询始发偏线月报表*/

    from STV.T_STL_MVR_PLR SP



  		SP.PERIOD=#{period,jdbcType=VARCHAR}



  		 AND  SP.PRODUCT_CODE=#{productCode,jdbcType=VARCHAR}




  		AND SP.CUSTOMER_CODE=#{customerCode,jdbcType=VARCHAR}




  		AND SP.ORIG_ORG_CODE=#{origOrgCode,jdbcType=VARCHAR}






		   and exists (select 1
	         from bse.mv_bas_user_org_auth_view uov
	        where uov.emp_code = #{userCode}
	          and uov.Org_Code = SP.DEST_ORG_CODE

		          and uov.Org_Code = #{destOrgCode,jdbcType=VARCHAR}

	          )

select /*结算-凭证报表-根据条件查询始发偏线月报表总条数，总金额*/ count(1) count, SUM(PL_COST_VECH /100) plCostVech, SUM(PL_COST_CONFIRM /100) plCostConfirm, SUM(PL_COST_NOT_CONFIRM /100) plCostNotConfirm, SUM(PL_COST_WO_DEST_RCV_POD /100) plCostWoDestRcvPod,
SUM(PL_COST_WO_DEST_RCV_NPOD /100) plCostWoDestRcvNpod,
SUM(PL_COST_PAY_APPLY /100) plCostPayApply, SUM(UR_DEST_CH_NPOD /100) urDestChNpod, SUM(UR_DEST_CD_NPOD /100) urDestCdNpod, SUM(UR_DEST_CH_POD /100) urDestChPod, SUM(UR_DEST_CD_POD /100) urDestCdPod, SUM(PL_DR_WO_DEST_RCV_POD /100) plDrWoDestRcvPod, SUM(PL_DR_WO_DEST_RCV_NPOD /100) plDrWoDestRcvNpod,
SUM(PL_DR_CH /100) plDrCh, SUM(PL_DR_CD /100) plDrCd, SUM(PL_DR_PAY_APPLY/100) plDrPayApply
from STV.T_STL_MVR_PLR SP
SP.PERIOD=#{period}
AND SP.PRODUCT_CODE=#{productCode,jdbcType=VARCHAR}
AND SP.CUSTOMER_CODE=#{customerCode,jdbcType=VARCHAR}
AND SP.ORIG_ORG_CODE=#{origOrgCode,jdbcType=VARCHAR}
and exists (select 1 from bse.mv_bas_user_org_auth_view uov where uov.emp_code = #{userCode} and uov.Org_Code = SP.DEST_ORG_CODE
and uov.Org_Code = #{destOrgCode,jdbcType=VARCHAR}
)
