mapper.select:
 SELECT /*结算-凭证报表-根据条件查询始发专线往来月报表*/

 FROM STV.T_STL_MVR_RFI
 WHERE PERIOD = #{period}

		AND ORG_TYPE = #{orgType}


		AND ORG_CODE = #{deptCode}


		AND CUSTOMER_CODE =#{customerCode}

SELECT /*结算-凭证报表-根据条件查询始发专线往来月报表总条数，总金额*/ count(1) COUNT, SUM(UR_DEST_CH_NPOD / 100) urDestChNpod, SUM(UR_DEST_CD_NPOD / 100) urDestCdNpod, SUM(UR_DEST_CH_POD / 100) urDestChPod, SUM(UR_DEST_CD_POD / 100) urDestCdPod, SUM(CLAIM_DEST_WO_INCOME / 100) claimDestWoIncome, SUM(CLAIM_WO_DEST_RCV_POD / 100) claimWoDestRcvPod, SUM(CLAIM_WO_DEST_RCV_NPOD /100) claimWoDestRcvNpod, SUM(COD_DEST_RCV_POD / 100) codDestRcvPod, SUM(COD_DEST_RCV_NPOD / 100) codDestRcvNpod, SUM(COD_ORIG_RCV_POD / 100) codOrigRcvPod, SUM(COD_ORIG_RCV_NPOD / 100) codOrigRcvNpod, SUM(COD_UR_CH_NPOD / 100) codUrChNpod, SUM(COD_UR_CD_NPOD / 100) codUrCdNpod, SUM(COD_WO_OR_RCV / 100) codWoOrRcv, SUM(RD_DEST_WO_INCOME / 100) rdDestWoIncome,
SUM(CUST_DR_DEST_RCV_NPOD) / 100 CUST_DR_DEST_RCV_NPOD, SUM(CUST_DR_DEST_RCV_POD) / 100 CUST_DR_DEST_RCV_POD, SUM(RD_WO_DEST_RCV_POD) / 100 RD_WO_DEST_RCV_POD, SUM(RD_WO_DEST_RCV_NPOD) / 100 RD_WO_DEST_RCV_NPOD FROM STV.T_STL_MVR_RFI T WHERE PERIOD = #{period}
AND ORG_TYPE = #{orgType}
AND ORG_CODE = #{deptCode}
AND CUSTOMER_CODE =#{customerCode}
