mapper.select:
select rep.gaprep_no diffReportNo, rep.unload_task_no unloadTaskNo, task.vehicle_no vehicleNo, task.unload_type unloadType, rep.exceed_goods_qty moreGoodsPieces, rep.lack_goods_qty lackGoodsPieces, rep.byhand_goods_qty byhandGoodsPieces, rep.handle_status handleStatus, loadMan.Loader_Name loadManName, loadMan.Loader_Code loadManCode, nvl(em.name,'') loadManDeptName, rep.create_time reportCreateTime from tfr.T_OPT_UNLOAD_GAPREP rep left join tfr.T_OPT_UNLOAD_TASK task on task.id = rep.unload_task_id left join tfr.T_OPT_LOADER_PARTICIPATION loadMan on task.id = loadMan.Task_Id and loadMan.Be_Creator = 'Y' left join (select e.emp_code, e.emp_name, o.code, o.name from bse.t_bas_employee e inner join bse.t_bas_org o ON o.code = e.org_code where e.active = 'Y' AND o.active = 'Y') em on em.emp_code = loadMan.Loader_Code
rep.Unload_Org_Code = #{orgCode}
and exists (select distinct de.unload_gaprep_id from tfr.t_opt_unload_gaprep_detail de where de.waybill_no = #{waybillNo} and rep.id= de.unload_gaprep_id)
and rep.create_time between #{beginCreateTime} and #{endCreateTime}
and rep.gaprep_no = #{diffReportNo}
and task.vehicle_no = #{vehicleNo}
and rep.unload_task_no = #{unloadTaskNo}
and rep.handle_status = #{handleStatus}
and loadMan.LOADER_CODE = #{loadManCode}

		select count(1)
			 from tfr.T_OPT_UNLOAD_GAPREP rep 
			      left join tfr.T_OPT_UNLOAD_TASK task 
			      on task.id = rep.unload_task_id
			      left join tfr.T_OPT_LOADER_PARTICIPATION  loadMan
			      on task.id = loadMan.Task_Id and loadMan.Be_Creator = 'Y'

			 rep.Unload_Org_Code = #{orgCode} 

				and exists (select distinct de.unload_gaprep_id  
                      from tfr.t_opt_unload_gaprep_detail de
                      where de.waybill_no = #{waybillNo}
                      and rep.id= de.unload_gaprep_id)


				and rep.create_time between #{beginCreateTime} and #{endCreateTime}


				and rep.gaprep_no = #{diffReportNo}


				and task.vehicle_no = #{vehicleNo}


				and rep.unload_task_no = #{unloadTaskNo}


				and rep.handle_status = #{handleStatus}


				and loadMan.LOADER_CODE = #{loadManCode}

mapper.insert:
begin
insert into tfr.t_opt_unload_gaprep (ID, UNLOAD_TASK_ID, GAPREP_NO, UNLOAD_TASK_NO, EXCEED_GOODS_QTY, LACK_GOODS_QTY, BYHAND_GOODS_QTY, HANDLE_STATUS, CREATE_TIME, UNLOAD_ORG_CODE) values( #{item.id,jdbcType=VARCHAR}, #{item.unloadTaskId,jdbcType=VARCHAR}, #{item.diffReportNo,jdbcType=VARCHAR}, #{item.unloadTaskNo,jdbcType=VARCHAR}, #{item.moreGoodsPieces,jdbcType=DECIMAL}, #{item.lackGoodsPieces,jdbcType=DECIMAL}, #{item.byhandGoodsPieces,jdbcType=DECIMAL}, #{item.handleStatus,jdbcType=VARCHAR}, #{item.reportCreateTime,jdbcType=TIMESTAMP}, #{item.orgCode,jdbcType=VARCHAR})
;end;

begin
insert into tfr.t_opt_unload_gaprep_detail (ID, UNLOAD_GAPREP_ID, WAYBILL_NO, SERIAL_NO, TRANSPORT_TYPE, DIFFERENCE_TYPE, BE_SCANNED, UNLOAD_TIME, CREATE_TIME, UNLOAD_ORG_CODE ) values (#{item.id,jdbcType=VARCHAR}, #{item.diffReportId,jdbcType=VARCHAR}, #{item.waybillNo,jdbcType=VARCHAR}, #{item.serialNo,jdbcType=VARCHAR}, #{item.transProperty,jdbcType=VARCHAR}, #{item.diffType,jdbcType=VARCHAR}, #{item.isScanedLastTime,jdbcType=VARCHAR}, #{item.unloadTime,jdbcType=TIMESTAMP}, #{item.reportCreatedTime,jdbcType=TIMESTAMP}, #{item.unloadOrgCode,jdbcType=VARCHAR})
;end;

mapper.select:
select gd.id id, gd.unload_gaprep_id diffReportId, gd.waybill_no waybillNo, gd.serial_no serialNo, gd.oa_error_no oaMistakeBillNo, gd.handle_time exceptionHandleTime from tfr.t_opt_unload_gaprep_detail gd
gd.difference_type in ('LACKGOODS','BYHANDGOODS') and gd.handle_time is null
and gd.unload_gaprep_id = #{diffReportId}
and gd.waybill_no = #{waybillNo}
and gd.serial_no = #{serialNo}
and gd.unload_org_code = #{unloadOrgCode}

