#{item,jdbcType=VARCHAR}
and d.unload_gaprep_id = g.id )
and not exists (select 1 from TFR.T_OPT_UNLOAD_GAPREP_ID_TEMP p where p.UNLOAD_GAPREP_ID = g.id)
union all
select g.id from tfr.t_opt_unload_gaprep g left join tfr.t_opt_unload_task task on g.unload_task_id = task.id where g.handle_status = #{handleStatus}
and g.CREATE_TIME > sysdate - 7 and task.unload_type = #{unloadDeliverType,jdbcType=VARCHAR}
