#{item,jdbcType=VARCHAR}
and d.unload_gaprep_id = g.id ) and not exists (select 1 from TFR.T_OPT_UNLOAD_GAPREP_ID_TEMP p where p.UNLOAD_GAPREP_ID = g.id)


select id, waybill_no as waybillNo, serial_no as serialNo, difference_type as diffType,vehicle_no AS vehicleNo, unloadTaskId from 
(
	select d.id, d.waybill_no, d.serial_no, d.difference_type,ut.vehicle_no, ut.id unloadTaskId from tfr.t_opt_unload_gaprep_detail d 
	left join tfr.t_opt_unload_gaprep ug on ug.id=d.unload_gaprep_id
	left join tfr.t_opt_unload_task ut on ut.id = ug.unload_task_id
	where d.unload_gaprep_id = #{reportId} 
	and d.difference_type in

		#{item,jdbcType=VARCHAR}

	and d.handle_time is null

	and (sysdate - cast(d.create_time as date))*24 > #{timeLimit}


)
order by waybill_no

	 select count(1) as num
  	 from tfr.t_opt_in_stock st
 	 where st.waybill_no = #{waybillNo,jdbcType=VARCHAR}
     and st.serial_no = #{serialNo,jdbcType=VARCHAR}
     and st.org_code = #{deptCode,jdbcType=VARCHAR}

     and st.in_stock_time >= #{taskTime,jdbcType=TIMESTAMP}       
     and st.in_stock_time <= sysdate 

     and st.is_valid = 'Y'

mapper.update:

	 update tfr.t_opt_unload_gaprep g
  set g.need_report_oa = 'L'
  where g.id = #{reportId,jdbcType=VARCHAR}

mapper.select:
select count(1) from ( select d.id from tfr.t_opt_unload_gaprep_detail d left join tfr.t_opt_unload_gaprep ug on ug.id=d.unload_gaprep_id left join tfr.t_opt_unload_task ut on ut.id = ug.unload_task_id where d.unload_gaprep_id = #{reportId,jdbcType=VARCHAR} and d.difference_type in
#{item,jdbcType=VARCHAR}
and d.handle_time is null
)

select count(1) From tfr.t_opt_unload_gaprep_detail d left join Tfr.T_Opt_Load_Waybill_Detail Lwd on lwd.waybill_no = d.waybill_no left join tfr.T_Opt_Load_Serialno s on S.Load_Waybill_Detail_Id = Lwd.Id and s.serial_no = d.serial_no Where d.create_time < s.load_time And Lwd.Task_Begin_Time Is Not Null
And S.Goods_State <> 'CANCELED'
and s.scan_state = 'SCANED' and d.serial_no = #{serialNo,jdbcType=VARCHAR} and d.waybill_no = #{waybillNo,jdbcType=VARCHAR} and d.unload_gaprep_id = #{unloadGaprepId,jdbcType=VARCHAR}

 select Count(1) from pkp.t_srv_labeled_good d
		where d.waybill_no = #{waybillNo,jdbcType=VARCHAR}
		and d.serial_no = #{serialNo,jdbcType=VARCHAR}
		and d.active = 'Y'

select count(1) from tfr.t_opt_unload_task os where os.unload_start_time =(select max(nk.unload_start_time) from tfr.t_opt_unload_waybill_detail ml left join tfr.t_opt_unload_task nk on ml.unload_task_id = nk.id where ml.waybill_no = #{wayBillNo}) and not exists
(select 1 from Tfr.t_Opt_Unload_Serialno so left join tfr.t_opt_unload_waybill_detail wl on so.unload_waybill_detail_id = wl.id left join tfr.t_opt_unload_task uk on wl.unload_task_id = uk.id where wl.waybill_no = #{wayBillNo} and uk.unload_start_time >= os.unload_start_time and uk.unload_start_time <= sysdate and so.scan_state = 'SCANED'
) and not exists
(Select 1 From Tfr.t_Opt_In_Stock ts Where ts.Waybill_No = #{wayBillNo} And ts.Is_Valid = 'Y' and ts.in_stock_type in ('SPECIAL_AREA', 'PACKAGE_AREA', 'VALUABLE_AREA', 'EXCEPTION_AREA','LOSS_GOODS_FIND','SEND_RETURN','PARTIALLINE_RETURN','AIR_RETURN') and ts.in_stock_time >= os.unload_start_time and ts.in_stock_time <= sysdate
) and not exists
(select 1 from TFR.T_OPT_ST_RESULT_LIST ad left join tfr.t_opt_st_task ps on ad.st_task_id = ps.id where ad.waybill_no = #{wayBillNo} and ps.createtime >= os.unload_start_time and ps.createtime <= sysdate and ad.scan_status in('DONE','MANUAL'))
