mapper.select:
SELECT /*结算-代理管理-按时间进行查询*/ W.WAYBILL_NO AS WAYBILL_NO,BASORG.NAME AS RECEIVE_ORG_CODE,W.BILL_TIME AS BILL_DATE,W.PAID_METHOD AS PAYMENT_METHOD, B.REGISTER_TIME AS HANDOVER_TIME,B.AGENT_COMPANY_NAME AS AGENCY_COMPANY_NAME,W.GOODS_QTY_TOTAL AS AMOUNT,W.GOODS_WEIGHT_TOTAL AS TOTAL_WEIGHT, W.GOODS_VOLUME_TOTAL AS TOTAL_VOLUME,W.TARGET_ORG_CODE AS TARGET_ORG_NAME,B.AUDIT_STATUS AS AUDIT_STATUS, B.WAYBILL_NO AS REGISTER_FLAG ,W.LAST_LOAD_ORG_CODE FROM PKP.T_SRV_WAYBILL W INNER JOIN TFR.T_OPT_HANDOVERBILL_DETAIL HDETIAL ON W.WAYBILL_NO=HDETIAL.WAYBILL_NO INNER JOIN TFR.T_OPT_HANDOVERBILL H ON H.HANDOVER_NO=HDETIAL.HANDOVER_NO AND H.HANDOVER_TYPE='LDP_HANDOVER' and h.handoverbill_state IN(20,30) INNER JOIN BSE.T_BAS_ORG BASORG ON BASORG.CODE = W.RECEIVE_ORG_CODE AND BASORG.ACTIVE = #{active} LEFT JOIN TFR.T_OPT_LDP_EXTERNAL_BILL B on W.WAYBILL_NO = B.WAYBILL_NO WHERE W.ACTIVE='Y'
AND W.CREATE_TIME>=#{ startBusinessDate ,jdbcType=TIMESTAMP} AND W.CREATE_TIME <#{endBusinessDate ,jdbcType=TIMESTAMP}
AND HDETIAL.HANDOVER_TYPE = #{productType}
AND B.REGISTER_TIME>=#{ handOverStartTime ,jdbcType=TIMESTAMP} AND B.REGISTER_TIME <#{handOverEndTime,jdbcType=TIMESTAMP}
AND W.LAST_LOAD_ORG_CODE = #{lastLoadOrgCode}
AND W.WAYBILL_NO = #{waybillNo}
AND B.WAYBILL_NO IS NOT NULL
AND B.WAYBILL_NO IS NULL
AND 1=1
AND EXISTS (SELECT V.ORG_CODE FROM BSE.MV_BAS_USER_ORG_AUTH_VIEW V WHERE V.EMP_CODE = #{empCode} AND V.ORG_CODE=W.LAST_LOAD_ORG_CODE )
ORDER BY W.CREATE_TIME ASC

SELECT /*结算-代理管理-按时间进行查询*/ count(*) FROM PKP.T_SRV_WAYBILL W INNER JOIN TFR.T_OPT_HANDOVERBILL_DETAIL HDETIAL ON W.WAYBILL_NO=HDETIAL.WAYBILL_NO INNER JOIN TFR.T_OPT_HANDOVERBILL H ON H.HANDOVER_NO=HDETIAL.HANDOVER_NO AND H.HANDOVER_TYPE='LDP_HANDOVER' and h.handoverbill_state IN(20,30) INNER JOIN BSE.T_BAS_ORG BASORG ON BASORG.CODE = W.RECEIVE_ORG_CODE AND BASORG.ACTIVE = #{active} LEFT JOIN TFR.T_OPT_LDP_EXTERNAL_BILL B on W.WAYBILL_NO = B.WAYBILL_NO WHERE W.ACTIVE='Y'
AND W.CREATE_TIME>=#{ startBusinessDate ,jdbcType=TIMESTAMP} AND W.CREATE_TIME <#{endBusinessDate ,jdbcType=TIMESTAMP}
AND HDETIAL.HANDOVER_TYPE = #{productType}
AND B.REGISTER_TIME>=#{ handOverStartTime ,jdbcType=TIMESTAMP} AND B.REGISTER_TIME <#{handOverEndTime,jdbcType=TIMESTAMP}
AND W.LAST_LOAD_ORG_CODE = #{lastLoadOrgCode}
AND W.WAYBILL_NO = #{waybillNo}
AND B.WAYBILL_NO IS NOT NULL
AND B.WAYBILL_NO IS NULL
AND 1=1
AND EXISTS (SELECT V.ORG_CODE FROM BSE.MV_BAS_USER_ORG_AUTH_VIEW V WHERE V.EMP_CODE = #{empCode} AND V.ORG_CODE=W.LAST_LOAD_ORG_CODE )
ORDER BY W.CREATE_TIME ASC
